import { useReducer } from "react";
import AccountInfoContext from "./accountInfo--context";

function reducer(state, action) {
  switch (action.type) {
    case "name": {
      let valid = true;
      if (action.value === 0) valid = false;

      return { ...state, name: action.value, valid };
    }

    case "backgroundColor": {
      return { ...state, background: action.value };
    }

    case "email": {
      let valid = true;
      if (action.value === 0 || !action.value.include("@")) valid = false;
      return { ...state, email: action.value, valid };
    }

    default:
      return state;
  }
}

function AccountInfoProvider(props) {
  const [accountInfo, setAccountInfo] = useReducer(reducer, {
    name: "Ryotsu Kankichi",
    background: "rgb(244, 164, 96)",
    email: "dscvwevew@gmail.com",
    valid: true,
  });

  return (
    <AccountInfoContext.Provider value={{ accountInfo, setAccountInfo }}>
      {props.children}
    </AccountInfoContext.Provider>
  );
}

export default AccountInfoProvider;
