import { Fragment, useContext } from "react";
import style from "./ExpenseListModal.module.css";
import ExpenseList from "../ExpenseList/ExpenseList";
import SubTitle from "../SubTitle/SubTitle";
import Button from "../Button";
import ModalShownContext from "../../../store/modalShown/modalShown--context";

function ExpenseListModal(props) {
  const ModalShownCtx = useContext(ModalShownContext);

  console.log(props.data);

  let time;
  let noData = false;
  if (typeof props.data === "string") {
    time = `${props.data.slice(0, 4)}-${props.data.slice(
      4,
      6
    )}-${props.data.slice(6)}`;
    noData = true;
  } else time = props.data[0].time;

  ModalShownCtx.setDateOnForm(time);

  let scrollClassName;
  if (props.data.length > 6) scrollClassName = "scroll";

  function backClickHandler() {
    ModalShownCtx.setExpenseListModalShown(false);
  }

  function addClickHandler() {
    ModalShownCtx.setExpenseListModalShown(false);
    ModalShownCtx.setAddDataFormModalShown(true);
  }

  let description;

  if (props.data.description.length > 10)
    description = props.data.description
      .split("")
      .filter((element, index) => index < 10)
      .join("")
      .padEnd(20, ".");

  let mainContent = noData ? (
    <SubTitle className={style.noData}>no data</SubTitle>
  ) : (
    <ExpenseList
      classItem={
        scrollClassName
          ? `${style.expenseList} ${style[scrollClassName]}`
          : `${style.expenseList}`
      }
      data={props.data}
    />
  );

  return (
    <Fragment>
      <SubTitle className={style.title}>{time}</SubTitle>
      <hr className={style.line}></hr>
      <div className={style["expenseList__container"]}>{mainContent}</div>

      <div className={style["btn__container"]}>
        <Button onClick={backClickHandler} type="button" className={style.btn}>
          back
        </Button>

        <Button onClick={addClickHandler} type="button" className={style.btn}>
          add data
        </Button>
      </div>
    </Fragment>
  );
}

export default ExpenseListModal;

