import { useRef, useEffect, useContext } from "react";
import ExpenseDataContext from "../../../../store/expenseData/expenseData--context";
import style from "./ChartMain.module.css";
import Chart from "chart.js/auto";

const dateOptObj = { month: "short" };

// const data1 = {
//   labels: ["Red", "Blue", "Yellow"],
//   datasets: [
//     {
//       label: "My First Dataset",
//       data: [300, 50, 100],
//       backgroundColor: [
//         "rgb(255, 99, 132)",
//         "rgb(54, 162, 235)",
//         "rgb(255, 205, 86)",
//       ],
//       hoverOffset: 4,
//     },
//   ],
// };

// const config1 = {
//   type: "doughnut",
//   data: data1,
// };

function ChartMain(props) {
  const chartRef = useRef(null);
  const { EXPENSE_DATA } = useContext(ExpenseDataContext);

  let configPPP;
  if (props.chartData.mainType === "time") {
    let labels = [],
      data = [];

    const standardLabels = createStandardLabelsArr(
      props.chartData.timeDuration,
      props.chartData.startingDate
    );

    labels = createLabelsArr(standardLabels, props.chartData.timeDuration);

    data = createDataArr(
      standardLabels,
      createFilteredData(standardLabels, EXPENSE_DATA),
      props.chartData.timeDuration,
      props.chartData.mainCategory,
      props.chartData.subCategory
    );

    const dataObj = {
      labels: labels,
      datasets: [
        {
          label: "",
          data: data,
          backgroundColor: [
            "rgba(255, 99, 132, 0.2)",
            "rgba(255, 159, 64, 0.2)",
            "rgba(255, 205, 86, 0.2)",
            "rgba(75, 192, 192, 0.2)",
            "rgba(54, 162, 235, 0.2)",
            "rgba(153, 102, 255, 0.2)",
            "rgba(201, 203, 207, 0.2)",
          ],
          borderColor: [
            "rgb(255, 99, 132)",
            "rgb(255, 159, 64)",
            "rgb(255, 205, 86)",
            "rgb(75, 192, 192)",
            "rgb(54, 162, 235)",
            "rgb(153, 102, 255)",
            "rgb(201, 203, 207)",
          ],
          borderWidth: 1,
        },
      ],
    };

    const config = {
      type: "bar",
      data: dataObj,
      options: {
        scales: {
          y: {
            beginAtZero: true,
          },
        },
      },
    };

    configPPP = config;
  } else {
    const filteredData = EXPENSE_DATA.filter(
      (element) =>
        Number(new Date(element.time)) >=
          Number(new Date(props.chartData.startingDate)) &&
        Number(new Date(element.time)) <=
          Number(new Date(props.chartData.endingDate)) &&
        element.category === props.chartData.mainCategory
    );

    const labels = props.chartData.subCategory;
    let newFilteredData = [];
    labels.forEach((label) => {
      newFilteredData.push(
        filteredData.filter((data) => data.mainCate === label)
      );
    });

    let dataArr = [];
    newFilteredData.forEach((data) => {
      let dataTmp;

      if (data.length > 0) {
        dataTmp = data.reduce((acc, cur) => (acc += Number(cur.price)), 0);
      } else dataTmp = 0;

      dataArr.push(dataTmp);
    });

    const data1 = {
      labels: labels,
      datasets: [
        {
          label: "My First Dataset",
          data: dataArr,
          backgroundColor: [
            "rgb(255, 99, 132)",
            "rgb(255, 159, 64)",
            "rgb(255, 205, 86)",
            "rgb(75, 192, 192)",
            "rgb(54, 162, 235)",
            "rgb(153, 102, 255)",
            "rgb(201, 203, 207)",
          ],
          hoverOffset: 4,
        },
      ],
    };

    const config1 = {
      type: "doughnut",
      data: data1,
    };

    configPPP = config1;

    // console.log(da);

    // console.log(labels);
  }

  useEffect(() => {
    const chart = new Chart(chartRef.current, configPPP);

    return function cleanUp() {
      chart.destroy();
    };
  }, [props.chartData]);

  let charClassName = "chart--bar";
  if (props.chartData.mainType === "category") charClassName = "chart--circle";

  return (
    <div className={style.chartMain}>
      <canvas className={style[charClassName]} ref={chartRef}></canvas>
    </div>
  );
}

export default ChartMain;

function createConfig(
  mainType,
  timeDuration,
  startingDate,
  expensData,
  mainCategory,
  subCategory
) {
  if (mainType === "time") {
    const standardLabels = createStandardLabelsArr(timeDuration, startingDate);
    const labels = createLabelsArr(standardLabels, timeDuration);
    const data = createDataArr(
      standardLabels,
      createFilteredData(standardLabels, expensData),
      timeDuration,
      mainCategory,
      subCategory
    );

    return {
      type: "bar",
      data: dataObj,
      options: {
        scales: {
          y: {
            beginAtZero: true,
          },
        },
      },
    };
  }
}

function createLabelsArr(standardLabels, duration) {
  if (duration === "6" || duration === "12") {
    return standardLabels.map((label) =>
      new Intl.DateTimeFormat("en-US", dateOptObj).format(new Date(label))
    );
  }

  return standardLabels.map((label) => `${label.slice(5, 7)}${label.slice(8)}`);
}

function createFilteredData(standardLabels, expensData) {
  const numStartingDate = Number(new Date(standardLabels[0]));
  const numEndingDate = Number(
    new Date(standardLabels[standardLabels.length - 1])
  );

  return expensData
    .filter(
      (element) =>
        Number(new Date(element.time)) >= numStartingDate &&
        Number(new Date(element.time)) <= numEndingDate
    )
    .sort(
      (elementA, elementB) =>
        Number(new Date(elementA.time)) - Number(new Date(elementB.time))
    );
}

function createDataArr(
  standardLabels,
  filteredData,
  timeDuration,
  mainCategory,
  subCategory
) {
  if (timeDuration === "12" || timeDuration === "6")
    return createDataArrMonth(
      standardLabels,
      filteredData,
      mainCategory,
      subCategory
    );
  else
    return createDataArrDay(
      standardLabels,
      filteredData,
      mainCategory,
      subCategory
    );
}

function createDataArrDay(
  standardLabels,
  filteredData,
  mainCategory,
  subCategory
) {
  const data = [];
  const checkboxNoChange =
    (mainCategory === "expense" && subCategory.length === 6) ||
    (mainCategory === "income" && subCategory.length === 3);
  let indexData = 0;
  let indexCurrentDate = true;
  let currentAmount = 0;
  let currentLabel;

  for (let i = 0; i < standardLabels.length; i++) {
    currentLabel = Number(new Date(standardLabels[i]));

    while (indexCurrentDate) {
      if (Number(new Date(filteredData[indexData]?.time)) <= currentLabel) {
        if (filteredData[indexData].category === mainCategory) {
          if (
            checkboxNoChange ||
            subCategory.includes(filteredData[indexData].mainCate)
          )
            currentAmount += Number(filteredData[indexData].price);
        }

        indexData++;
      } else indexCurrentDate = false;
    }

    data.push(currentAmount);
    currentAmount = 0;
    indexCurrentDate = true;
  }

  return data;
}

function createDataArrMonth(
  standardLabels,
  filteredData,
  mainCategory,
  subCategory
) {
  const data = [];
  const checkboxNoChange =
    (mainCategory === "expense" && subCategory.length === 6) ||
    (mainCategory === "income" && subCategory.length === 3);
  let indexData = 0;
  let indexCurrentDate = true;
  let currentAmount = 0;
  let yearLabel, monthLabel, dayLabel, yearDate, monthData, dayData;

  for (let i = 0; i < standardLabels.length; i++) {
    yearLabel = standardLabels[i].slice(0, 4);
    monthLabel = standardLabels[i].slice(5, 7);
    dayLabel = standardLabels[i].slice(8);

    while (indexCurrentDate) {
      yearDate = filteredData[indexData]?.time.slice(0, 4);
      monthData = filteredData[indexData]?.time.slice(5, 7);
      dayData = filteredData[indexData]?.time.slice(8);

      if (yearLabel === yearDate && monthLabel === monthData) {
        if (Number(dayLabel) <= Number(dayData) && 31 >= Number(dayData))
          if (filteredData[indexData].category === mainCategory) {
            if (
              checkboxNoChange ||
              subCategory.includes(filteredData[indexData].mainCate)
            )
              currentAmount += Number(filteredData[indexData].price);
          }

        indexData++;
      } else indexCurrentDate = false;
    }

    data.push(currentAmount);
    currentAmount = 0;
    indexCurrentDate = true;
  }

  return data;
}

function createStandardLabelsArr(duration, startingDate) {
  const today = new Date(startingDate);

  let labels = [];

  // one week
  if (duration === "7") {
    createStandardLabelsArrHelper(labels, 7, 1, "day", today);
  }

  if (duration === "14") {
    createStandardLabelsArrHelper(labels, 14, 1, "day", today);
  }

  if (duration === "30") {
    createStandardLabelsArrHelper(labels, 30, 1, "day", today);
  }

  if (duration === "90") {
    createStandardLabelsArrHelper(labels, 30, 3, "day", today);
  }

  if (duration === "6") {
    createStandardLabelsArrHelper(labels, 6, 1, "month", today);
  }

  if (duration === "12") {
    createStandardLabelsArrHelper(labels, 12, 1, "month", today);
  }

  return labels;
}

function createStandardLabelsArrHelper(
  labels,
  duration,
  incrementAmount,
  dayMonth,
  today
) {
  let tmpDay = today;
  let day;
  let month;
  let year = today.getFullYear();
  let changeYear = false;

  // if it's increment by month, the day is never change
  // we could put it outisde for-loop
  if (dayMonth === "month") {
    if (tmpDay.getDate() < 10) day = `0${tmpDay.getDate()}`;
    else day = tmpDay.getDate();
  }

  for (let i = 0; i < duration; i++) {
    // incremenet by month
    if (dayMonth === "month")
      tmpDay.setMonth(tmpDay.getMonth() + incrementAmount);

    // increment by day
    if (dayMonth === "day") {
      tmpDay.setDate(tmpDay.getDate() + incrementAmount);

      // day will change, so put this inside for-loop
      if (tmpDay.getDate() < 10) day = `0${tmpDay.getDate()}`;
      else day = tmpDay.getDate();
    }

    if (tmpDay.getMonth() + 1 < 10) month = `0${tmpDay.getMonth() + 1}`;
    else month = tmpDay.getMonth() + 1;

    // increment year
    if (changeYear) {
      year++;
      changeYear = false;
    }
    if (tmpDay.getMonth() === 11) changeYear = true;

    labels.push(`${year}-${month}-${day}`);
  }
