import { useEffect, useState, useRef } from "react";
import AccountInfo from "../../../UI/AccountInfo/AccountInfo";
import style from "./Weekly.module.css";

import Chart from "chart.js/auto";

const currentTime = new Date();

const optionDemo = {
  // hour: 'numeric',
  // minute: 'numeric',
  day: "numeric",
  month: "short", // long, numeric, 2-digit
  year: "numeric", // 2-digit
  weekday: "short", // long, numeric, 2-digit
};

const time = new Intl.DateTimeFormat("en-US", optionDemo).format(currentTime);

const labels = ["MON", "TUE", "WED", "THR", "FRI", "SUN", "SAT"];

const data = {
  labels: labels,
  datasets: [
    {
      label: "My First Dataset",
      data: [65, 59, 80, 81, 56, 55, 40],
      backgroundColor: [
        "rgba(255, 99, 132, 0.2)",
        "rgba(255, 159, 64, 0.2)",
        "rgba(255, 205, 86, 0.2)",
        "rgba(75, 192, 192, 0.2)",
        "rgba(54, 162, 235, 0.2)",
        "rgba(153, 102, 255, 0.2)",
        "rgba(201, 203, 207, 0.2)",
      ],
      borderColor: [
        "rgb(255, 99, 132)",
        "rgb(255, 159, 64)",
        "rgb(255, 205, 86)",
        "rgb(75, 192, 192)",
        "rgb(54, 162, 235)",
        "rgb(153, 102, 255)",
        "rgb(201, 203, 207)",
      ],
      borderWidth: 1,
    },
  ],
};

const config = {
  type: "bar",
  data: data,
  options: {
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  },
};

const data1 = {
  labels: ["Red", "Blue", "Yellow"],
  datasets: [
    {
      label: "My First Dataset",
      data: [300, 50, 100],
      backgroundColor: [
        "rgb(255, 99, 132)",
        "rgb(54, 162, 235)",
        "rgb(255, 205, 86)",
      ],
      hoverOffset: 4,
    },
  ],
};

const config1 = {
  type: "doughnut",
  data: data1,
};

function Weekly() {
  const chartRef = useRef(null);
  const chartRef1 = useRef(null);

  return (
    <div className={style.weekly}>
      <AccountInfo
        title="Overall Account Info"
        income="1000"
        expense="600"
        net="400"
      />
      <div className={style["chart"]}>
        <div className={style["chart__container"]}>
          {/* <canvas
            ref={chartRef}
            className={style["chart"]}
            height="200"
            width="auto"
          ></canvas> */}
        </div>
        <div className={style["chart__container"]}>
          {/* <canvas
            ref={chartRef1}
            className={style["chart1"]}
            height="200"
            width="auto"
          ></canvas> */}
        </div>
      </div>
    </div>
  );
}

export default Weekly;
