import { useState, Fragment, useEffect, useContext } from "react";

import Title from "../../../UI/Title/Title";
import CalendarList from "./CalendarList";
import Button from "../../../UI/Button";
import ExpenseItem from "./ExpenseItem";
import time from "../../../Others/time";
import FormContext from "../../../../store/formShown/form--context";
import { TiPlus } from "react-icons/ti";
import { v4 as uuidv4 } from "uuid";
import style from "./Daily.module.css";

const currentDate = new Date();
const CURRENT_DATE = new Date();

let currentTime = time(currentDate);

const EXPENSE_DATA = [
  {
    category: "expense",
    mainCate: "food",
    subCate: "breakfast",
    time: "2021-08-01",
    description: "Burger",
    money: "50",
  },
  {
    category: "expense",
    mainCate: "clothing",
    subCate: "t-shirt",
    time: "2021-04-11",
    money: "100",
  },
  {
    category: "expense",
    mainCate: "housing",
    subCate: "bill",
    time: "2021-10-23",
    money: "500",
  },
  {
    category: "expense",
    mainCate: "transportation",
    subCate: "bus",
    time: "2021-02-15",
    description: "Burger",
    money: "10",
  },
  {
    category: "income",
    mainCate: "salary",
    subCate: "salary",
    time: "2021-09-13",
    money: "3000",
  },
  {
    category: "expense",
    mainCate: "housing",
    subCate: "bill",
    time: "2021-09-08",
    money: "500",
  },
  {
    category: "expense",
    mainCate: "food",
    subCate: "dinner",
    time: "2021-09-01",
    description: "Burger",
    money: "110",
  },
  {
    category: "income",
    mainCate: "stock",
    subCate: "stock",
    time: "2021-09--04",
    money: "100",
  },
  {
    category: "expense",
    mainCate: "education",
    subCate: "book",
    time: "2021-09-11",
    money: "50",
  },
  {
    category: "income",
    mainCate: "salary",
    subCate: "salary",
    time: "2021-09-18",
    money: "1000",
  },
  {
    category: "expense",
    mainCate: "entertainment",
    subCate: "video games",
    time: "2021-09-23",
    description: "Burger",
    money: "110",
  },
  {
    category: "income",
    mainCate: "bonus",
    subCate: "bonus",
    time: "2021-09-12",
    money: "500",
  },
  {
    category: "expense",
    mainCate: "food",
    subCate: "BreakFast",
    time: "2021-09-22",
    description: "Burger",
    money: "110",
  },
  {
    category: "income",
    mainCate: "others",
    subCate: "others",
    time: "2021-09-21",
    money: "100",
  },
];
let index = 0;

function Daily() {
  const [calendarDate, setCalendatDate] = useState(currentTime);
  const [animation, setAnimation] = useState(false);
  const formCtx = useContext(FormContext);

  useEffect(() => {
    if (index === 0) {
      index++;
      return;
    }

    setAnimation(true);

    const time = setTimeout(() => {
      setAnimation(false);
    }, 300);

    return function cleanUp() {
      clearTimeout(time);
    };
  }, [calendarDate]);

  function incrementDateHandler() {
    currentDate.setDate(currentDate.getDate() + 7);
    currentTime = time(currentDate);
    setCalendatDate(currentTime);
  }

  function dncrementDateHandler() {
    currentDate.setDate(currentDate.getDate() - 7);
    currentTime = time(currentDate);
    setCalendatDate(currentTime);
  }

  function showFormHandler() {
    formCtx.setModalShown(true);
  }

  let active = null;

  const calendarList = calendarDate.map((time) => {
    if (
      time.month === CURRENT_DATE.getMonth() &&
      time.monthDay === CURRENT_DATE.getDate()
    )
      active = true;
    else active = false;

    return (
      <CalendarList
        key={uuidv4()}
        weekDay={time.weekDay}
        monthDay={time.monthDay}
        active={active}
        animation={animation}
      />
    );
  });

  const expenseItem = EXPENSE_DATA.map((expense, i) => {
    return (
      <Fragment key={uuidv4()}>
        <ExpenseItem
          category={expense.category}
          mainCate={expense.mainCate}
          subCate={expense.subCate}
          time={expense.time}
          description={expense.description}
          money={expense.money}
        />
        {i === EXPENSE_DATA.length - 1 || (
          <hr className={style["item__line"]}></hr>
        )}
      </Fragment>
    );
  });

  return (
    <div className={style.daily}>
      <div className={style["daily__first"]}>
        <Title>daily transection</Title>
        <div className={style["btn__container"]}>
          <Button onClick={showFormHandler} className={style.btn}>
            {<TiPlus />}
          </Button>
        </div>
      </div>

      <div className={style["calendar__container"]}>
        <Button
          type={"button"}
          className={style["btn__calendar__list"]}
          onClick={dncrementDateHandler}
        >
          {"<"}
        </Button>
        {calendarList}
        <Button
          type={"button"}
          className={style["btn__calendar__list"]}
          onClick={incrementDateHandler}
        >
          {">"}
        </Button>
      </div>

      <div className={style["today__account"]}>
        <div>
          <p className={style["today__account__text"]}>Total Expense</p>
          <p className={style["today__account__number"]}>$110</p>
        </div>
        <div className={style.income}>
          <p className={style["today__account__text"]}>Total Income</p>
          <p className={style["today__account__number"]}>$110</p>
        </div>
        <div>
          <p className={style["today__account__text"]}>Total Net</p>
          <p className={style["today__account__number"]}>$110</p>
        </div>
      </div>

      <div className={style.item}>
        <ul className={style["item__container"]}>{expenseItem}</ul>
      </div>
    </div>
  );
}

export default Daily;
