/*
The logic is 
1. If the year of data is less than current year, we can guarantee this data is within the range, so directly return true
*/
function compareTime(data, timeObj, curYear, curMonth, curDay) {
  if (!timeObj)
    return (
      data.year < curYear ||
      (data.year === curYear && data.month <= curMonth && data.day <= curDay)
    );
  else
    return (
      data.year - 0 < timeObj.getFullYear() ||
      (data.year - 0 === timeObj.getFullYear() &&
        data.month - 0 <= timeObj.getMonth() + 1 &&
        data.day - 0 <= timeObj.getDate())
    );
}

export default compareTime;
