import AccountSection from "../../../UI/AccountSection/AccountSection";

import style from "./MonthlyInfo.module.css";

const labels = ["MON", "TUE", "WED", "THR", "FRI", "SUN", "SAT"];

const configArea = {
  type: "line",
  data: {
    labels: labels,
    datasets: [
      {
        fill: {
          target: "origin",
        },
        label: "My First Dataset",
        data: [65, 59, 80, 81, 56, 55, 40],
        backgroundColor: ["rgba(54, 162, 235, 0.2)"],
        borderColor: ["rgb(54, 162, 235)"],
        borderWidth: 1,
        pointBackgroundColor: ["rgb(54, 162, 235)"],
      },
    ],
  },
  options: {
    elements: {
      line: {
        tension: 0.5,
      },
    },
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  },
};

const configLine = {
  type: "line",
  data: {
    labels: labels,
    datasets: [
      {
        label: "My First Dataset",
        data: [65, 59, 80, 81, 56, 55, 40],
        backgroundColor: ["rgba(54, 162, 235, 0.2)"],
        borderColor: ["rgb(54, 162, 235)"],
        borderWidth: 1,
        pointBackgroundColor: ["rgb(54, 162, 235)"],
      },
    ],
  },
  options: {
    elements: {
      line: {
        tension: 0.1,
      },
    },
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  },
};

const configBar = {
  type: "bar",
  data: {
    labels: labels,
    datasets: [
      {
        label: "My First Dataset",
        data: [65, 59, 80, 81, 56, 55, 40],
        backgroundColor: ["rgba(54, 162, 235, 0.2)"],
        borderColor: ["rgb(54, 162, 235)"],
        borderWidth: 1,
        pointBackgroundColor: ["rgb(54, 162, 235)"],
      },
    ],
  },
  options: {
    indexAxis: "y",
  },
};

//

const configPie = {
  type: "pie",
  data: {
    labels: ["Red", "Blue", "Yellow"],
    datasets: [
      {
        label: "My First Dataset",
        data: [300, 50, 100],
        backgroundColor: [
          "rgb(255, 99, 132)",
          "rgb(54, 162, 235)",
          "rgb(255, 205, 86)",
        ],
        hoverOffset: 4,
      },
    ],
  },
};

const chartConfigArr = [configLine, configArea, configBar, configPie];

function CalendarInfo(props) {
  return (
    <div>
      <AccountSection
        title="Monthly Overview"
        income="1000"
        expense="600"
        configArr={chartConfigArr}
        classSubTitle={style.subTitle}
      />
    </div>
  );
}

export default CalendarInfo;
