import { useRef, useEffect, useState } from "react";
import Card from "../Card/Card";
import SubTitle from "../SubTitle/SubTitle";
import style from "./ChartSmall.module.css";
import Chart from "chart.js/auto";

function ChartSmall(props) {
  const [chart, setChart] = useState(0);

  function selectChangeHandler(e) {
    setChart(e.target.value);
  }

  const chartRef = useRef(null);
  useEffect(() => {
    const chart = new Chart(chartRef.current, props.config);

    return function cleanUp() {
      chart.destroy();
    };
  }, []);

  return (
    <Card className={style["chart__section"]}>
      <div className={style["title__section"]}>
        <SubTitle>Chart</SubTitle>
        <select onChange={selectChangeHandler} className={style.select}>
          <option value="0">line chart</option>
          <option value="1">area chart</option>
          <option value="2">bar chart</option>
          <option value="3">pie chart</option>
        </select>
      </div>
      <p>ddd</p>
      <div className={style["chart__container"]}>
        <canvas
          ref={chartRef}
          className={style["chart"]}
          height="200"
          width="auto"
        ></canvas>
      </div>
    </Card>
  );
}

export default ChartSmall;
