import { Fragment, useRef, useEffect } from "react";
import AccountInfo from "../AccountInfo/AccountInfo";

import style from "./AccountSection.module.css";

import Chart from "chart.js/auto";

function AccountSection(props) {
  const chartRef = useRef(null);
  const chartRef1 = useRef(null);

  useEffect(() => {
    const chart = new Chart(chartRef.current, props.config);

    const chart1 = new Chart(chartRef1.current, props.config1);

    return function cleanUp() {
      chart.destroy();
      chart1.destroy();
    };
  }, []);

  return (
    <Fragment>
      <AccountInfo
        title={props.title}
        income={props.income}
        expense={props.expense}
        net={props.income - props.expense}
      />
      <div className={style["chart"]}>
        <div className={style["chart__container"]}>
          {/* <canvas
            ref={chartRef}
            className={style["chart"]}
            height="200"
            width="auto"
          ></canvas> */}
        </div>
        <div className={style["chart__container"]}>
          {/* <canvas
            ref={chartRef1}
            className={style["chart1"]}
            height="200"
            width="auto"
          ></canvas> */}
        </div>
      </div>
    </Fragment>
  );
}

export default AccountSection;
