import { useContext } from "react";
import Modal from "../Modal/Modal";
import Title from "../Title/Title";
import SubTitle from "../SubTitle/SubTitle";
import HorizontalLine from "../HorizontalLine/HorizontalLine";
import Button from "../Button";
import ExpenseList from "../ExpenseList/ExpenseList";
import ExpenseDataContext from "../../../store/expenseData/expenseData--context";
import style from "./DeleteCategoryModal.module.css";

function DeleteCategoryModal(props) {
  const { expenseData } = useContext(ExpenseDataContext);

  let expenseItem;
  // main category
  if (props.mainCategoryArr.includes(props.clickingCategoryForDelete)) {
    expenseItem = expenseData.filter(
      (element) => element.mainCate === props.clickingCategoryForDelete
    );
  } else {
    expenseItem = expenseData.filter(
      (element) => element.subCate === props.clickingCategoryForDelete
    );
  }

  let subtitleContent;
  if (expenseItem.length > 0)
    subtitleContent = `there are still ${expenseItem.length} data in your expense history`;
  else subtitleContent = "there's no data in your expense history";
  return (
    <Modal>
      <div className={style["title__container"]}>
        <Title className={style.title}>are you sure to delete</Title>
        <Title className={style.title}>food category?</Title>
      </div>
      <HorizontalLine />
      <SubTitle className={style.subtitle}>
        there's no way getting back once you delete it
      </SubTitle>
      {
        <ExpenseList
          modal={true}
          classItem={style.expenseList}
          data={expenseItem}
        />
      }
      <div>
        <Button>cancel</Button>
        <Button>remove</Button>
      </div>
    </Modal>
  );
}

export default DeleteCategoryModal;
