import { Fragment } from "react";
import ReactDOM from "react-dom";

import Title from "../SubTitle/SubTitle";
import InputCheckbox from "../InputCheckbox/InputCheckbox";
import InputRadio from "../InputRadio/InputRadio";
import Button from "../Button";

import style from "./Modal.module.css";

const EXPENSE_CATEGORY = {
  food: ["breakfast", "brunch", "lunch", "dinner", "snack", "drink", "others"],
  clothing: ["clothes", "pants", "accessories", "underwear", "others"],
  housing: [
    "rent",
    "water  bill",
    "electricity bill",
    "gas bill",
    "cable bill",
    "Internet bill",
  ],
};

function PopupModal() {
  return (
    <div className={style.overlay}>
      <div className={style.modal}>
        <form className={style.form}>
          <div className={style["title__container"]}>
            <InputRadio
              classInput={style.input}
              classCheck={`${style.expense} ${style.check}`}
              classLabel={style.title}
              label="expense"
              name="title"
            />
            <InputRadio
              classInput={style.input}
              classCheck={`${style.income} ${style.check}`}
              classLabel={style.title}
              label="income"
              name="title"
            />
          </div>
          <hr className={style.line}></hr>
          <div className={style["form__container"]}>
            <div className={style["container"]}>
              <label className={style.label}>main category</label>
              <select className={style.input} type="select">
                <option>food</option>
                <option>clothing</option>
                <option>housing</option>
                <option>transportation</option>
                <option>education</option>
                <option>entertainment</option>
                <option>others</option>
              </select>
            </div>

            <div className={style["container"]}>
              <label className={style.label}>sub category</label>

              <select className={style.input} type="select">
                <option>food</option>
                <option>clothing</option>
                <option>housing</option>
                <option>transportation</option>
                <option>education</option>
                <option>entertainment</option>
                <option>others</option>
              </select>
            </div>
            <div className={style["container"]}>
              <label className={style.label}>description (optional)</label>
              <input className={style.input} type="text"></input>
            </div>

            <div className={style["container"]}>
              <label className={style.label}>time</label>
              <input className={style.input} type="date"></input>
            </div>

            <div
              className={`${style["container"]} ${style["container--last"]}`}
            >
              <label className={style.label}>price</label>
              <input className={style.input} type="number"></input>
            </div>

            <div className={`${style["btn__container"]} `}>
              <Button className={style.btn}>cancel</Button>
              <Button className={style.btn}>add</Button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
}

function Modal(props) {
  return (
    <Fragment>
      {ReactDOM.createPortal(<PopupModal />, document.querySelector("#modal"))}
    </Fragment>
  );
}

export default Modal;
