const dateOptions = {
  weekday: "short",
};

function time(currentDate) {
  const dateArr = [];

  for (let i = 0; i < 7; i++) {
    const date = new Date(
      currentDate.setDate(currentDate.getDate() - currentDate.getDay() + i)
    );

    dateArr.push({
      weekDay: new Intl.DateTimeFormat("en-US", dateOptions)
        .format(date)
        .toUpperCase(),
      monthDay: date.getDate(),
      month: date.getMonth(),
      dateObj: date,
    });
  }

  return dateArr;
}

export default time;
