function FormatMoney(price) {
  let formatedPrice = price;
  if (Math.abs(price) > 1000000) {
    formatedPrice = `${(price / 1000000).toFixed(2)}M`;
  } else if (Math.abs(price) > 1000000000) {
    formatedPrice = `${(price / 1000000000).toFixed(2)}B`;
  } else if (price > 1000) {
    const priceStr = String(price);
    formatedPrice = "";
    let commaIndex = 0;

    for (let i = priceStr.length - 1; i >= 0; i++) {
      if (commaIndex % 3 === 0) formatedPrice += `,${priceStr[i]}`;
      else formatedPrice += priceStr[i];

      commaIndex++;
    }
  }

  return formatedPrice;
}

export default FormatMoney;

/*
1M = 1Million = 1,000,000 = 1百万. 1B = 1Billion = 1,000,000,000 = 10亿.
*/
