import { useState } from "react";

import style from "./SearchOptionTime.module.css";

import InputCheckbox from "../../../../UI/InputCheckbox/InputCheckbox";

import { IoIosArrowDroprightCircle } from "react-icons/io";
import { IoIosArrowDropdownCircle } from "react-icons/io";

const checkboxTime = [
  "today",
  "yesterady",
  "three days ago",
  "a week ago",
  " a month ago",
  "three months ago",
  "six months ago",
  "a year ago",
];

function SearchOptionUI(props) {
  const [icon, setIcon] = useState(false);
  const [checkState, setCheckState] = useState(
    new Array(checkboxTime.length).fill(false)
  );

  function changeIconHandler() {
    setIcon((prev) => !prev);
  }

  function changeChekboxHandler(index) {
    setCheckState(checkState.map((check, i) => (index === i ? !check : check)));
  }

  const checkboxContent = props.checkboxItem.map((checkbox, i) => (
    <InputCheckbox
      label={checkbox}
      id={checkbox}
      key={checkbox}
      checked={checkState[i]}
      onChange={() => changeChekboxHandler(i)}
      classCheck={style.check}
      classInput={style.input}
      classLabel={style.labelItem}
    />
  ));

  return (
    <div>
      <InputCheckbox
        classCheck={style.icon}
        classLabel={style.label}
        classContainer={style["icon__container"]}
        icon={
          icon ? <IoIosArrowDroprightCircle /> : <IoIosArrowDropdownCircle />
        }
        label="time"
        id="time"
        checked={icon}
        onChange={changeIconHandler}
      />
      <div
        className={
          icon ? `${style.container}  ${style.hidden}` : `${style.container}`
        }
      >
        {checkboxContent}
      </div>
    </div>
  );
}

export default SearchOptionUI;
