import { useRef, useEffect, useState, useContext } from "react";
import InputRadio from "../../../UI/InputRadio/InputRadio";
import ExpenseDataContext from "../../../../store/expenseData/expenseData--context";
import createDateFormat from "../../../../Others/createDateFormat";
import createFilteredData from "../../../../Others/CreateChartData/createFilteredData";
import style from "./AccountChart.module.css";
import Chart from "chart.js/auto";

// for choosing diff time range (input radio)
const timeRangeArr = ["1wk", "1mo", "3mo", "6mo", "1yr"];

const TODAY = new Date();

// first day of the week
const initialDate = new Date(new Date().setDate(new Date().getDate() - 7));

// an array of labels(string)
const initialLabels = createLabelsAccount(initialDate, TODAY);

function AccountChart() {
  const { expenseData } = useContext(ExpenseDataContext);
  const [chartData, setChartData] = useState({
    data: createChartDataArr(initialLabels, expenseData),
    labels: initialLabels,
  });
  const chartRef = useRef(null);

  const config = {
    type: "line",
    data: {
      labels: chartData.labels,
      datasets: [
        {
          fill: {
            target: "origin",
          },
          label: "",
          data: chartData.data,
          backgroundColor: ["rgba(54, 162, 235, 0.2)"],
          borderColor: ["rgb(54, 162, 235)"],
          borderWidth: 1,
          pointBackgroundColor: ["rgb(54, 162, 235)"],
        },
      ],
    },
    options: {
      elements: {
        line: {
          tension: 0.5,
        },
      },
      scales: {
        y: {
          beginAtZero: true,
        },
      },
    },
  };

  useEffect(() => {
    const chart = new Chart(chartRef.current, config);

    return function cleanUp() {
      chart.destroy();
    };
  }, [chartData]);

  // helper function for changing time range
  function chartRadioChangeHandler(e) {
    const today = new Date();
    const value = e.target.value;

    if (value === "1wk") today.setDate(today.getDate() - 7);
    if (value === "1mo") today.setMonth(today.getMonth() - 1);
    if (value === "3mo") today.setMonth(today.getMonth() - 3);
    if (value === "6mo") today.setMonth(today.getMonth() - 6);
    if (value === "1yr") today.setFullYear(today.getFullYear() - 1);

    const labels = createLabelsAccount(today, TODAY);

    const chartData = createChartDataArr(labels, expenseData);

    setChartData({ data: chartData, labels });
  }

  return (
    <div className={style["chart__container"]}>
      <div
        onChange={chartRadioChangeHandler}
        className={style["radio__container"]}
      >
        {timeRangeArr.map((element, index) => (
          <InputRadio
            key={element}
            label={element}
            id={element}
            value={element}
            name="chart__btn"
            defaultChecked={index === 0}
            classLabel={style.label}
            classInput={style.input}
            classCheck={style.check}
          />
        ))}
      </div>
      <div>
        <canvas className={style.chart} ref={chartRef}></canvas>
      </div>
    </div>
  );
}

export default AccountChart;

function createChartDataArr(labels, expenseData) {
  const newExpenseData = createFilteredData(labels, expenseData);

  const prevTotalAccAmount =
    createPreAccAmount("income", expenseData, labels[0]) -
    createPreAccAmount("expense", expenseData, labels[0]);

  const newExpenseDataExpense = newExpenseData.filter(
    (element) => element.category === "expense"
  );
  const newExpenseDataIncome = newExpenseData.filter(
    (element) => element.category === "income"
  );

  const dataArrExpense = createDataArrAccount(labels, newExpenseDataExpense);
  const dataArrIncome = createDataArrAccount(labels, newExpenseDataIncome);

  const finalDataArr = [];

  dataArrExpense.forEach((expense, index) => {
    finalDataArr.push(dataArrIncome[index] - expense + prevTotalAccAmount);
  });

  return finalDataArr;
}

function createDataArrAccount(labels, expensData) {
  const data = [];
  let indexCurrentDate = true;
  let currentAmount = 0;
  let indexData = 0;
  let yearLabel, monthLabel, dayLabel, yearDate, monthData, dayData;

  for (let i = 0; i < labels.length; i++) {
    yearLabel = labels[i].slice(0, 4);
    monthLabel = labels[i].slice(5, 7);
    dayLabel = labels[i].slice(8);

    while (indexCurrentDate) {
      yearDate = expensData[indexData]?.time.slice(0, 4);
      monthData = expensData[indexData]?.time.slice(5, 7);
      dayData = expensData[indexData]?.time.slice(8);

      if (
        yearLabel === yearDate &&
        monthLabel === monthData &&
        dayLabel >= dayData
      ) {
        currentAmount += Number(expensData[indexData].price);
        indexData++;
      } else indexCurrentDate = false;
    }

    data.push(currentAmount);
    indexCurrentDate = true;
  }

  return data;
}

function createLabelsAccount(startDate, endDate) {
  const labels = [];

  // caculate the difference between two dates
  const diff = Math.round(
    (new Date(endDate).getTime() - new Date(startDate).getTime()) /
      (1000 * 3600 * 24)
  );

  /*
  there's always seven labels which means it can only have seven timestamp
  so have to first know whether the difference can be divided by 7
  */
  let eachDiffAmount;
  let lastDiffAmount = 0;
  if (diff % 7 === 0) eachDiffAmount = diff / 7;
  else {
    eachDiffAmount = (diff - (diff % 7)) / 7;
    lastDiffAmount = diff - eachDiffAmount * 6;
  }

  let tmpDate;
  for (let i = 0; i < 6; i++) {
    tmpDate = new Date(startDate.setDate(startDate.getDate() + eachDiffAmount));
    labels.push(createDateFormat(tmpDate));
  }

  console.log(labels);

  if (lastDiffAmount === 0)
    tmpDate = new Date(startDate.setDate(startDate.getDate() + eachDiffAmount));
  else
    tmpDate = new Date(startDate.setDate(startDate.getDate() + lastDiffAmount));

  labels.push(createDateFormat(tmpDate));

  return labels;
}

function createPreAccAmount(type, expenseData, startingDate) {
  const yearData = Number(startingDate.slice(0, 4));
  const monthData = Number(startingDate.slice(5, 7));
  const dayData = Number(startingDate.slice(8));

  return expenseData
    .filter(
      (element) =>
        element.category === type &&
        Number(element.time.slice(0, 4)) <= yearData &&
        Number(element.time.slice(5, 7)) <= monthData &&
        Number(element.time.slice(8)) <= dayData
    )
    .reduce((acc, cur) => acc + Number(cur.price), 0);
}
