import FormContainer from "./FormContainer";
import Select from "../Select/Select";
import style from "./AddDataForm.module.css";

function FormSubCategory(props) {
  let newSelectArr = props.subCategoryArr;
  if (props.edit) {
    newSelectArr = props.subCategoryArr.filter(
      (data) => data === props.subCategory
    );

    props.subCategoryArr.forEach((data) => {
      if (data !== props.subCategory) newSelectArr.push(data);
    });
  }

  return (
    <FormContainer>
      <label htmlFor="subCategory" className={`${style.label} capitalize`}>
        sub category
      </label>
      <Select
        onChange={props.subCategoryChangeHandler}
        className={style.input}
        name="subCategory"
        id="subCategory"
      >
        {newSelectArr.map((element) => (
          <option value={element} key={element}>
            {element}
          </option>
        ))}
      </Select>
    </FormContainer>
  );
}

export default FormSubCategory;
