import { useState, useReducer } from "react";
import style from "./Monthly.module.css";
import Button from "../../../UI/Button";
import createCalendar from "./createCalendar";

import { v4 as uuidv4 } from "uuid";

const dateOptObj = { month: "long" };

const date = new Date();

const calendar = createCalendar(date);

function CalendarMonthly(props) {
  //   const [calendartData, calendarDispatch] = useReducer(reducer, initialObj);
  const [calendarState, setcalendarState] = useState(calendar);
  const [month, setMonth] = useState(
    new Intl.DateTimeFormat("en-US", dateOptObj).format(date)
  );
  const [year, SetYear] = useState(date.getFullYear());

  function incrementMonth() {
    date.setMonth(date.getMonth() + 1);
    const calendar = createCalendar(date);

    setMonth(new Intl.DateTimeFormat("en-US", dateOptObj).format(date));
    setcalendarState(calendar);
    SetYear(date.getFullYear());
  }

  function decreaseMonth() {
    date.setMonth(date.getMonth() - 1);
    const calendar = createCalendar(date);

    setMonth(new Intl.DateTimeFormat("en-US", dateOptObj).format(date));
    setcalendarState(calendar);
    SetYear(date.getFullYear());
  }

  //   console.log("----");

  let firstOne = true;
  let lastOne = false;
  let index = true;
  let index2 = false;

  return (
    <div className={style.monthly}>
      <div className={style["monthly__container"]}>
        <div className={style["monthly__month"]}>
          <Button
            className={style.btn}
            onClick={decreaseMonth}
            type="button"
          >{`${"<"}`}</Button>
          <div className={style["monthly__title"]}>
            <h6>{month}</h6>
            <h6>{year}</h6>
          </div>
          <Button
            className={style.btn}
            onClick={incrementMonth}
            type="button"
          >{`${">"}`}</Button>
        </div>
        <div className={style["monthly__week"]}>
          <div>Sun</div>
          <div>Mon</div>
          <div>Tue</div>
          <div>Wed</div>
          <div>Thu</div>
          <div>Fri</div>
          <div>Sat</div>
        </div>
        <div className={style["monthly__days"]}>
          {calendarState.map((item, i) => {
            if (+item === 1 && !lastOne && firstOne) {
              index = false;
            }

            if (index) {
              return (
                <div className={style.negative} key={uuidv4()}>
                  {item}
                </div>
              );
            }

            return <div key={uuidv4()}>{item}</div>;
          })}
        </div>
      </div>
    </div>
  );
}
export default CalendarMonthly;

