import { Fragment } from "react";
import { MdMoreVert } from "react-icons/md";
import { useState } from "react";
import { AiFillDelete } from "react-icons/ai";
import { AiFillEdit } from "react-icons/ai";
import style from "./ExpenseItem.module.css";
import createIconObj from "../../../assets/createIconObj/createIconObj";
import Button from "../../../UI/Button";

const iconObj = createIconObj();

function ExpenseItem(props) {
  const [btnMore, setBtnMore] = useState(false);
  const [hideDescription, setHideDescription] = useState(false);

  const classMainCate =
    props.category === "expense"
      ? `${style["item__mainCate--blue"]}`
      : `${style["item__mainCate--pink"]}`;

  const icon = iconObj[props.mainCate];

  function btnMoreClickHandler() {
    setBtnMore((prev) => !prev);
  }

  let description =
    props.modal && props.description
      ? props.description
          .split("")
          .filter((element, index) => index < 5)
          .join("")
          .padEnd(8, ".")
      : props.description;

  let limitLength = props.modal ? 0 : 15;

  if (props.description.length > limitLength && btnMore)
    description = props.description
      .split("")
      .filter((element, index) => index < limitLength)
      .join("")
      .padEnd(limitLength + 5, ".");

  return (
    <Fragment>
      <li className={style["item__list"]}>
        {/* <div className={style["description--show"]}>
          <p>
            ewdwewdwewdwewdwewdwewdwewdwewdwewdewdwewdwewdwewdwwewdwdwedwedewdwedwedwewdwew
          </p>
        </div> */}
        <div className={style["item__info"]}>
          <div className={`${style["item__mainCate"]} ${classMainCate}`}>
            {icon}
          </div>
          <div>
            <p className={style.sub}>{props.subCate}</p>
            <p className={style.time}>{props.time}</p>
          </div>
          <p className={style.description}>{description}</p>
        </div>
        <div className={style.last}>
          <p className={style.money}>${props.price}</p>
          {btnMore && (
            <div className={style["btn__group__container"]}>
              <Button type="button" className={style["btn__group"]}>
                <AiFillDelete className={style.icon} /> delete
              </Button>
              <span className={style.vertical}></span>
              <Button type="button" className={style["btn__group"]}>
                <AiFillEdit className={style.icon} /> edit
              </Button>
            </div>
          )}
          <Button
            onClick={btnMoreClickHandler}
            type="button"
            className={style["btn__more"]}
          >
            <MdMoreVert />
          </Button>
        </div>
      </li>
    </Fragment>
  );
}

export default ExpenseItem;
