import { useRef, useEffect } from "react";
import style from "./ChartMain.module.css";
import Chart from "chart.js/auto";
const labels = [
  "MON",
  "TUE",
  "WED",
  "THR",
  "FRI",
  "SUN",
  "SAT",
  "test",
  "test",
  "test",
  "test",
  "test",
  "test",
];

const data = {
  labels: labels,
  datasets: [
    {
      label: "My First Dataset",
      data: [65, 59, 80, 81, 56, 55, 40, 100, 50, 100, 100, 100, 100],
      backgroundColor: [
        "rgba(255, 99, 132, 0.2)",
        "rgba(255, 159, 64, 0.2)",
        "rgba(255, 205, 86, 0.2)",
        "rgba(75, 192, 192, 0.2)",
        "rgba(54, 162, 235, 0.2)",
        "rgba(153, 102, 255, 0.2)",
        "rgba(201, 203, 207, 0.2)",
      ],
      borderColor: [
        "rgb(255, 99, 132)",
        "rgb(255, 159, 64)",
        "rgb(255, 205, 86)",
        "rgb(75, 192, 192)",
        "rgb(54, 162, 235)",
        "rgb(153, 102, 255)",
        "rgb(201, 203, 207)",
      ],
      borderWidth: 1,
    },
  ],
};

const config = {
  type: "bar",
  data: data,
  options: {
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  },
};

function ChartMain(props) {
  console.log(props.chartData);
  const chartRef = useRef(null);

  // useEffect(() => {
  //   const chart = new Chart(chartRef.current, config);

  //   return function cleanUp() {
  //     chart.destroy();
  //   };
  // }, []);

  console.log(props.chartData);
  return (
    <div className={style.chartMain}>
      <div>{/* <canvas ref={chartRef}></canvas> */}</div>
    </div>
  );
}

export default ChartMain;

function createLabelsArr(duration, startingDate) {
  const today = new Date(startingDate);

  let labels = [];

  // one week
  if (duration === "7") {
    let tmpDay = today;
    let day;
    let month;

    for (let i = 0; i < 7; i++) {
      tmpDay.setDate(tmpDay.getDate() + 1);

      if (tmpDay.getMonth() + 1 < 10) month = `0${tmpDay.getMonth() + 1}`;
      else month = tmpDay.getMonth() + 1;

      if (tmpDay.getDate() < 10) day = `0${tmpDay.getDate()}`;
      else day = tmpDay.getDate();

      labels.push(`${month}${day}`);
    }
  }

  if (duration === "14") {
    let tmpDay = today;
    let day;
    let month;

    for (let i = 0; i < 7; i++) {
      tmpDay.setDate(tmpDay.getDate() + 2);

      if (tmpDay.getMonth() + 1 < 10) month = `0${tmpDay.getMonth() + 1}`;
      else month = tmpDay.getMonth() + 1;

      if (tmpDay.getDate() < 10) day = `0${tmpDay.getDate()}`;
      else day = tmpDay.getDate();

      labels.push(`${month}${day}`);
    }
  }

  if (duration === "30") {
    let tmpDay = today;
    let day;
    let month;

    for (let i = 0; i < 30; i++) {
      tmpDay.setDate(tmpDay.getDate() + 1);

      if (tmpDay.getMonth() + 1 < 10) month = `0${tmpDay.getMonth() + 1}`;
      else month = tmpDay.getMonth() + 1;

      if (tmpDay.getDate() < 10) day = `0${tmpDay.getDate()}`;
      else day = tmpDay.getDate();

      labels.push(`${month}${day}`);
    }
  }

  if (duration === "90") {
    let tmpDay = today;
    let day;
    let month;

    for (let i = 0; i < 30; i++) {
      tmpDay.setDate(tmpDay.getDate() + 3);

      if (tmpDay.getMonth() + 1 < 10) month = `0${tmpDay.getMonth() + 1}`;
      else month = tmpDay.getMonth() + 1;

      if (tmpDay.getDate() < 10) day = `0${tmpDay.getDate()}`;
      else day = tmpDay.getDate();

      labels.push(`${month}${day}`);
    }
  }

  if (duration === "6") {
    let tmpDay = today;
    let day;
    let month;

    for (let i = 0; i < 30; i++) {
      tmpDay.setDate(tmpDay.getDate() + 3);

      if (tmpDay.getMonth() + 1 < 10) month = `0${tmpDay.getMonth() + 1}`;
      else month = tmpDay.getMonth() + 1;

      if (tmpDay.getDate() < 10) day = `0${tmpDay.getDate()}`;
      else day = tmpDay.getDate();

      labels.push(`${month}${day}`);
    }
  }

  console.log(labels);
}
