import { useState, useReducer } from "react";
import style from "./Monthly.module.css";
import Button from "../../../UI/Button";
import createCalendar from "./createCalendar";

import { v4 as uuidv4 } from "uuid";

const dateOptObj = { month: "long" };
const dateOptObj1 = { year: "numeric" };

const initialObj = {
  calendar: createCalendar(this.date),
  month: new Intl.DateTimeFormat("en-US", dateOptObj).format(date),
  year: date.getFullYear(),
};

const date = new Date();
const month = new Intl.DateTimeFormat("en-US", dateOptObj).format(date);

const calendar = createCalendar(date);
// console.log(new Intl.DateTimeFormat("en-US", dateOptObj).format(date));

function reducer(state, action) {
  switch (action.type) {
    case "INCREMENT": {
      if (state.month === "December") {
        date.setFullYear(date.getFullYear() + 1);
        date.setMonth(0);
      } else date.setMonth(date.getMonth() + 1);

      const calendar = createCalendar(date);
      const month = new Intl.DateTimeFormat("en-US", dateOptObj).format(date);
      const year = date.getFullYear();

      return {
        calendar,
        month,
        year,
      };
    }

    case "DECREMENT": {
      if (state.month === "January") {
        date.setFullYear(date.getFullYear() - 1);
        date.setMonth(0);
      } else date.setMonth(date.getMonth() - 1);

      const calendar = createCalendar(date);
      const month = new Intl.DateTimeFormat("en-US", dateOptObj).format(date);
      const year = date.getFullYear();

      return {
        calendar,
        month,
        year,
      };
    }

    default: {
      return state;
    }
  }
}

function CalendarMonthly(props) {
  const [calendarItem, setCalendarItem] = useState(calendar);
  const [monthState, setMonthState] = useState(month);

  const [calendatData, calendarDispatch] = useReducer(reducer, initialObj);

  function incrementMonth() {
    date.setMonth(date.getMonth() + 1);
    const calendar = createCalendar(date);
    const month = new Intl.DateTimeFormat("en-US", dateOptObj).format(date);

    setCalendarItem(calendar);
    setMonthState(month);
  }

  function decreaseMonth() {
    date.setMonth(date.getMonth() - 1);
    const calendar = createCalendar(date);
    const month = new Intl.DateTimeFormat("en-US", dateOptObj).format(date);
    setCalendarItem(calendar);
    setMonthState(month);
  }

  return (
    <div className={style.monthly}>
      <div className={style["monthly__container"]}>
        <div className={style["monthly__month"]}>
          <Button
            className={style.btn}
            onClick={decreaseMonth}
            type="button"
          >{`${"<"}`}</Button>
          <div className={style["monthly__title"]}>
            <h6>{monthState}</h6>
            <h6>
              {new Intl.DateTimeFormat("en-US", dateOptObj1).format(
                props.today
              )}
            </h6>
          </div>
          <Button
            className={style.btn}
            onClick={incrementMonth}
            type="button"
          >{`${">"}`}</Button>
        </div>
        <div className={style["monthly__week"]}>
          <div>Sun</div>
          <div>Mon</div>
          <div>Tue</div>
          <div>Wed</div>
          <div>Thu</div>
          <div>Fri</div>
          <div>Sat</div>
        </div>
        <div className={style["monthly__days"]}>
          {calendarItem.map((item) => (
            <div key={uuidv4()}>{item}</div>
          ))}
        </div>
      </div>
    </div>
  );
}
export default CalendarMonthly;
