import { useState } from "react";

import style from "./SearchOptionTime.module.css";

import InputCheckbox from "../../../../UI/InputCheckbox/InputCheckbox";

import { IoIosArrowDroprightCircle } from "react-icons/io";
import { IoIosArrowDropdownCircle } from "react-icons/io";

const checkboxTime = [
  "today",
  "yesterady",
  "three days ago",
  "a week ago",
  " a month ago",
  "three months ago",
  "six months ago",
  "a year ago",
];

function SearchOptionTime() {
  const [icon, setIcon] = useState(false);
  const [checkState, setCheckState] = useState(
    new Array(checkboxTime.length).fill(false)
  );

  function changeIconHandler() {
    setIcon((prev) => !prev);
  }

  function changeChekboxHandler(index) {
    setCheckState(
      checkState.map((check, i) => {
        if (index === i) return (check = !check);
      })
    );
  }

  const checkboxContent = checkboxTime.map((checkbox, i) => (
    <InputCheckbox
      label={checkbox}
      id={checkbox}
      key={checkbox}
      checked={checkState[i]}
    />
  ));

  return (
    <div>
      <InputCheckbox
        classCheck={style.icon}
        icon={
          icon ? <IoIosArrowDropdownCircle /> : <IoIosArrowDroprightCircle />
        }
        label="time"
        id="time"
        checked={icon}
        onChange={changeIconHandler}
      />
      <div>
        <InputCheckbox label="today" id="today" defaultChecked={false} />
        <InputCheckbox
          label="yesterday"
          id="yesterday"
          defaultChecked={false}
        />
        <InputCheckbox
          label="three days ago"
          id="three days ago"
          defaultChecked={false}
        />
        <InputCheckbox
          label="a week ago"
          id="a week ago"
          defaultChecked={false}
        />
        <InputCheckbox
          label="a month ago"
          id="a month ago"
          defaultChecked={false}
        />
        <InputCheckbox
          label="three months ago"
          id="three months ago"
          defaultChecked={false}
        />
        <InputCheckbox
          label="six months ago"
          id="six months ago"
          defaultChecked={false}
        />
        <InputCheckbox
          label="a year ago"
          id="a year ago"
          defaultChecked={false}
        />
      </div>
    </div>
  );
}

export default SearchOptionTime;
