import { useRef, useEffect, useState } from "react";
import Title from "../../../UI/Title/Title";
import style from "./AccountSmallChart.module.css";
import Card from "../../../UI/Card/Card";
import Chart from "chart.js/auto";

const labels = ["MON", "TUE", "WED", "THR", "FRI", "SUN", "SAT"];
const configBar = {
  type: "bar",
  data: {
    labels: labels,
    datasets: [
      {
        label: "My First Dataset",
        data: [65, 59, 80, 81, 56, 55, 40],
        backgroundColor: ["rgba(54, 162, 235, 0.2)"],
        borderColor: ["rgb(54, 162, 235)"],
        borderWidth: 1,
        pointBackgroundColor: ["rgb(54, 162, 235)"],
      },
    ],
  },
  options: {
    indexAxis: "y",
  },
};

function AccountSmallChart(props) {
  const chartRef = useRef(null);
  useEffect(() => {
    const chart = new Chart(chartRef.current, configBar);

    return function cleanUp() {
      chart.destroy();
    };
  }, []);

  return (
    <Card className={style["chart__section"]}>
      <div className={style["title__section"]}>
        <Title>Chart</Title>
        <select className={style.select}>
          <option value="0">which item expense the most</option>
          <option value="1">area chart</option>
          <option value="2">bar chart</option>
          <option value="3">pie chart</option>
        </select>
      </div>
      <div className={style["chart__container"]}>
        <canvas ref={chartRef} height="200" width="auto"></canvas>
      </div>
    </Card>
  );
}

export default AccountSmallChart;
