import compareTimeWithRange from "../compareTimeWithRange";

function createPieDataArr(
  startingDate,
  endingDate,
  expenseData,
  mainCategory,
  subCategory
) {
  const startingDateObj = new Date(startingDate);
  const endingDateObj = new Date(endingDate);
  const filteredData = expenseData.filter(
    (element) =>
      compareTimeWithRange(element, startingDateObj, endingDateObj) &&
      element.category === mainCategory
  );

  const labelsSet = new Set();
  for (let data of filteredData) {
    if (labelsSet.size === subCategory.length) break;
    labelsSet.add(data.mainCate);
  }
  const labels = Array.from(labelsSet);

  let newFilteredData = [];
  labels.forEach((label) => {
    newFilteredData.push(
      filteredData.filter((data) => data.mainCate === label)
    );
  });

  console.log(newFilteredData);

  const dataArr = [];

  newFilteredData.forEach((data) => {
    let dataTmp;

    if (data.length > 0) {
      dataTmp = data.reduce((acc, cur) => (acc += Number(cur.price)), 0);
    } else dataTmp = 0;

    dataArr.push(dataTmp);
  });

  return [labels, dataArr];
}

export default createPieDataArr;
