import { useReducer } from "react";
import CategoryContext from "./category--context";
const EXPENSE_CATEGORY = {
  food: ["breakfast", "brunch", "lunch", "dinner", "snack", "drink"],
  clothing: ["clothes", "pants", "shoes", "accessories", "underwear"],
  housing: [
    "rent",
    "water  bill",
    "electricity bill",
    "gas bill",
    "cable bill",
    "internet bill",
  ],
  transportation: ["gasoline", "parking", "maintenance"],
  education: ["books", "course"],
  entertainment: ["video games", "computer games", "party"],
  others: ["others"],
};

const INCOME_CATEGORY = {
  salary: ["salary", "bonus"],
  stock: ["stock"],
  others: ["others"],
};

function reducer(state, action) {
  switch (action.type) {
    case "REMOVE": {
      console.log("remove");
      console.log(action.value);
    }

    default:
      break;
  }
}

function CategoryProvider(props) {
  const reducerInitialObj = {
    categoryExpense: EXPENSE_CATEGORY,
    categoryIncome: INCOME_CATEGORY,
  };

  const [category, categoryDispatch] = useReducer(reducer, reducerInitialObj);

  function removeCategoryExpense(value) {
    categoryDispatch({ type: "REMOVE", value });
  }

  //   console.log("aaa");
  console.log(category);

  const contextInitialObj = {
    categoryExpense: EXPENSE_CATEGORY,
    categoryIncome: INCOME_CATEGORY,
    removeCategoryExpense,
  };

  return (
    <CategoryContext.Provider value={contextInitialObj}>
      {props.children}
    </CategoryContext.Provider>
  );
}

export default CategoryProvider;
