import { useState } from "react";
import SearchList from "./SearchList/SearchList";
import SearchOption from "./SearchOption/SearchOption";
import SearchListDataProvider from "../../../store/searchListData/SearchListDataProvider";
import style from "./Search.module.css";

function Search() {
  const [showSearchOptionModal, setShowSearchOptionModal] = useState(false);

  return (
    <SearchListDataProvider>
      <div className={style.search}>
        <div
          className={
            showSearchOptionModal ? `${style["search__option__container"]}` : ``
          }
        >
          <SearchOption />
        </div>
        <SearchList />
      </div>
    </SearchListDataProvider>
  );
}

export default Search;
