import { useState, useEffect } from "react";
import MainContent from "./components/MainContent/MainContent";
import SideBar from "./components/SideBar/SideBar";
import ExpenseDataProvider from "./store/expenseData/ExpenseDataProvider";
import EditModal from "./components/UI/EditModal/EditModal";
import useEditModal from "./components/Others/Custom/useEditModal";
import style from "./App.module.css";

const TODAY = new Date();

function App() {
  const [page, setPage] = useState(0);

  return (
    <ExpenseDataProvider>
      <div className={`${style["app__container"]} center`}>
        <SideBar today={TODAY} setPage={setPage} page={page} />
        <MainContent setEditModal={setEditModal} today={TODAY} page={page} />
      </div>
    </ExpenseDataProvider>
  );
}

export default App;

