import { useState, useEffect, useContext } from "react";
import useDailyExpenseList from "../../../Others/Custom/useDailyExpenseList";
import Title from "../../../UI/Title/Title";
import CalendarList from "./CalendarList";
import Button from "../../../UI/Button";
import ExpenseItem from "./ExpenseItem";
import time from "../../../Others/time";
import ModalShownContext from "../../../../store/modalShown/modalShown--context";
import ExpenseDataContext from "../../../../store/expenseData/expenseData--context";
import ExpenseList from "../../../UI/ExpenseList/ExpenseList";
import { TiPlus } from "react-icons/ti";
import style from "./Daily.module.css";
import { v4 as uuidv4 } from "uuid";

const currentDate = new Date();
const CURRENT_DATE = new Date();

let currentTime = time(currentDate);

let index = 0;

function Daily() {
  const [calendarDate, setCalendatDate] = useState(currentTime);
  const [animation, setAnimation] = useState(false);
  const modalShownCtx = useContext(ModalShownContext);
  const expenseDataCtx = useContext(ExpenseDataContext);
  const [dailyExpenseList, setDayState] = useDailyExpenseList(CURRENT_DATE);

  console.log(expenseDataCtx);

  useEffect(() => {
    if (index === 0) {
      index++;
      return;
    }

    setAnimation(true);

    const time = setTimeout(() => {
      setAnimation(false);
    }, 300);

    return function cleanUp() {
      clearTimeout(time);
    };
  }, [calendarDate]);

  function incrementDateHandler() {
    currentDate.setDate(currentDate.getDate() + 7);
    currentTime = time(currentDate);
    setCalendatDate(currentTime);
  }

  function dncrementDateHandler() {
    currentDate.setDate(currentDate.getDate() - 7);
    currentTime = time(currentDate);
    setCalendatDate(currentTime);
  }

  function showFormHandler() {
    modalShownCtx.setDateOnForm(CURRENT_DATE);
    modalShownCtx.setAddDataFormModalShown(true);
  }

  let active = null;

  const calendarList = calendarDate.map((time) => {
    if (
      time.month === CURRENT_DATE.getMonth() &&
      time.monthDay === CURRENT_DATE.getDate()
    )
      active = true;
    else active = false;

    return (
      <CalendarList
        key={uuidv4()}
        weekDay={time.weekDay}
        monthDay={time.monthDay}
        dateObj={time.dateObj}
        active={active}
        animation={animation}
        setDayState={setDayState}
      />
    );
  });

  return (
    <div className={style.daily}>
      <div className={style["daily__first"]}>
        <Title>daily transection</Title>
        <div className={style["btn__container"]}>
          <Button onClick={showFormHandler} className={style.btn}>
            {<TiPlus />}
          </Button>
        </div>
      </div>

      <div className={style["calendar__container"]}>
        <Button
          type={"button"}
          className={style["btn__calendar__list"]}
          onClick={dncrementDateHandler}
        >
          {"<"}
        </Button>
        {calendarList}
        <Button
          type={"button"}
          className={style["btn__calendar__list"]}
          onClick={incrementDateHandler}
        >
          {">"}
        </Button>
      </div>

      <div className={style["today__account"]}>
        <div>
          <p className={style["today__account__text"]}>Total Expense</p>
          <p className={style["today__account__number"]}>$110</p>
        </div>
        <div className={style.income}>
          <p className={style["today__account__text"]}>Total Income</p>
          <p className={style["today__account__number"]}>$110</p>
        </div>
        <div>
          <p className={style["today__account__text"]}>Total Net</p>
          <p className={style["today__account__number"]}>$110</p>
        </div>
      </div>

      <ExpenseList data={dailyExpenseList} classItem={style.item} />
    </div>
  );
}

export default Daily;
