import { useState, useEffect, useContext } from "react";
import MainContent from "./components/MainContent/MainContent";
import SideBar from "./components/SideBar/SideBar";
import ExpenseDataProvider from "./store/expenseData/ExpenseDataProvider";
import EditModalProvider from "./store/editModal/EditModalProvider";
import CategoryProvider from "./store/category/CategoryProvider";
import AccountInfoProvider from "./store/accountInfo/AccountInfoProvider";
import DisplayThemeContext from "./store/displayTheme/displayTheme--context";
import { FiChevronsLeft } from "react-icons/fi";
import { FiMenu } from "react-icons/fi";
import timeObj from "./components/assets/timeObj/timeObj";
import style from "./App.module.css";

import { doc, setDoc } from "firebase/firestore";
import { signInWithPopup } from "firebase/auth";
import { db, provider, auth } from "./firebase-config";

const { TODAY } = timeObj;

function App() {
  const [page, setPage] = useState(0);
  const [showSidebar, setShowSidebar] = useState(false);
  const [userInfo, setUserInfo] = useState({ userName: "", userGmail: "" });
  const { displayTheme } = useContext(DisplayThemeContext);

  useEffect(() => {
    if (displayTheme === "dark") {
      document.body.classList.remove("light");
      document.body.classList.add("dark");
    } else {
      document.body.classList.remove("dark");
      document.body.classList.add("light");
    }
  }, [displayTheme]);

  useEffect(async () => {
    const newUserName = userInfo.userName.split(" ").join("");
    const userID = `${userInfo.userGmail}${newUserName}`;
    await setDoc(doc(db, "users", userID), userInfo);
  }, [userInfo]);

  async function signeIntWithGoogle() {
    const res = await signInWithPopup(auth, provider);
    const { displayName, email } = res.user;
    setUserInfo({ userName: displayName, userGmail: email });
  }

  function menuClickHandler() {
    setShowSidebar((prev) => !prev);
  }

  return (
    <ExpenseDataProvider>
      <EditModalProvider>
        <CategoryProvider>
          <AccountInfoProvider>
            <div
              onClick={signeIntWithGoogle}
              className={`${style["app__container"]} center`}
            >
              {showSidebar ? (
                <FiChevronsLeft
                  tabIndex="0"
                  aria-label="show sidebar"
                  onClick={menuClickHandler}
                  className={style.icon}
                />
              ) : (
                <FiMenu
                  tabIndex="0"
                  aria-label="close sidebar"
                  onClick={menuClickHandler}
                  className={style.icon}
                />
              )}

              <SideBar
                today={TODAY}
                setPage={setPage}
                page={page}
                showSidebar={showSidebar}
                menuClickHandler={menuClickHandler}
              />
              <MainContent
                today={TODAY}
                page={page}
                showSidebar={showSidebar}
                menuClickHandler={menuClickHandler}
              />
            </div>
          </AccountInfoProvider>
        </CategoryProvider>
      </EditModalProvider>
    </ExpenseDataProvider>
  );
}

export default App;

