import { useContext, useReducer } from "react";
import EditModalContext from "../../../store/editModal/editModal--context";
import InputRadio from "../InputRadio/InputRadio";
import Button from "../Button";
import Modal from "../Modal/Modal";
import ExpenseDataContext from "../../../store/expenseData/expenseData--context";
import createDateFormat from "../../Others/createDateFormat";
import iconObj from "../../assets/iconObj/iconObj";
import { v4 as uuidv4 } from "uuid";

import style from "./AddDataForm.module.css";

const EXPENSE_CATEGORY = {
  food: ["breakfast", "brunch", "lunch", "dinner", "snack", "drink"],
  clothing: ["clothes", "pants", "shoes", "accessories", "underwear"],
  housing: [
    "rent",
    "water  bill",
    "electricity bill",
    "gas bill",
    "cable bill",
    "internet bill",
  ],
  transportation: ["gasoline", "parking", "maintenance"],
  education: ["books", "course"],
  entertainment: ["video games", "computer games", "party"],
  others: ["others"],
};

const INCOME_CATEGORY = {
  salary: ["salary", "bonus"],
  stock: ["stock"],
  others: ["others"],
};

function reducer(state, action) {
  switch (action.type) {
    case "CATEGORY": {
      console.log(action.value);
      let mainCategoryArr, subCategoryArr;

      if (action.value === "expense") {
        mainCategoryArr = Object.keys(EXPENSE_CATEGORY);
        subCategoryArr = EXPENSE_CATEGORY["food"];
      } else {
        mainCategoryArr = Object.keys(INCOME_CATEGORY);
        subCategoryArr = INCOME_CATEGORY["salary"];
      }

      return {
        ...state,
        category: action.value,
        mainCategoryArr,
        subCategoryArr,
        mainCategory: "salary",
        subCategory: "salary",
      };
    }

    case "MAIN_CATEGORY": {
      let subCategoryArr;
      if (state.category === "expense")
        subCategoryArr = EXPENSE_CATEGORY[action.value];
      else subCategoryArr = INCOME_CATEGORY[action.value];

      return { ...state, mainCategory: action.value, subCategoryArr };
    }

    case "SUB_CATEGORY": {
      return { ...state, subCategory: action.value };
    }

    case "DESCRIPTION": {
      return { ...state, description: action.value };
    }

    case "DATE": {
      return { ...state, date: action.value };
    }

    case "PRICE": {
      let valid = false;
      if (
        action.value.trim().length > 0 &&
        !Object.is(-0, Number(action.value)) &&
        Number(action.value) >= 0
      )
        valid = true;

      return { ...state, isValid: valid, price: action.value };
    }

    default:
      break;
  }
}

function AddDataForm(props) {
  let initialObj;

  if (props.initialObj)
    initialObj = {
      ...props.initialObj,
      mainCategoryArr:
        props.initialObj.category === "expense"
          ? Object.keys(EXPENSE_CATEGORY)
          : Object.keys(INCOME_CATEGORY),
      subCategoryArr:
        props.initialObj.category === "expense"
          ? EXPENSE_CATEGORY[props.initialObj.mainCategory]
          : INCOME_CATEGORY[props.initialObj.mainCategory],
    };
  else
    initialObj = {
      category: "expense",
      mainCategoryArr: Object.keys(EXPENSE_CATEGORY),
      mainCategory: "food",
      subCategoryArr: EXPENSE_CATEGORY["food"],
      subCategory: "breakfast",
      date:
        props.date === undefined
          ? createDateFormat(new Date())
          : createDateFormat(new Date(props.date)),
      description: "",
      price: "",
      icon: iconObj["food"],
      isValid: false,
    };

  const [formData, formDataDispatch] = useReducer(reducer, initialObj);
  const { addExpenseData, editExpenseData } = useContext(ExpenseDataContext);
  const [, setEditModal] = useContext(EditModalContext);

  // get rid of animation, so that component won't re-render several times repeatedly
  // const [animation, setAnimation] = useState(false);
  // useEffect(() => {
  //   setAnimation(formData.category);

  //   const time = setTimeout(() => {
  //     setAnimation(false);
  //   }, 500);

  //   return function cleanUp() {
  //     clearTimeout(time);
  //   };
  // }, [formData.category]);

  function categoryChangeHandler(e) {
    formDataDispatch({ type: "CATEGORY", value: e.target.value });
  }

  function mainCategoryChangeHandler(e) {
    formDataDispatch({
      type: "MAIN_CATEGORY",
      value: e.target.value,
    });
  }

  function subCategoryChangeHandler(e) {
    formDataDispatch({
      type: "SUB_CATEGORY",
      value: e.target.value,
    });
  }

  function descriptionChangeHandler(e) {
    formDataDispatch({
      type: "DESCRIPTION",
      value: e.target.value,
    });
  }

  function dateChangeHandler(e) {
    formDataDispatch({
      type: "DATE",
      value: e.target.value,
    });
  }

  function priceChangeHandler(e) {
    formDataDispatch({
      type: "PRICE",
      value: e.target.value,
    });
  }

  function cancelClickHandler() {
    if (props.closeAddDataFormHandler) props.closeAddDataFormHandler();

    if (props.closeFormHandler) props.closeFormHandler();

    if (props.closeAddDataFormHandlerExpenseItem)
      props.closeAddDataFormHandlerExpenseItem();
  }

  function formSubmitHandler(e) {
    e.preventDefault();

    const newFormData = {
      id: props.initialObj ? props.initialObj.id : uuidv4(),
      category: formData.category,
      mainCate: formData.mainCategory,
      subCate: formData.subCategory,
      time: formData.date,
      description: formData.description,
      price: formData.price,
    };

    if (props.initialObj) editExpenseData(newFormData);
    else addExpenseData(newFormData);

    if (props.closeAddDataFormHandler) props.closeAddDataFormHandler();

    if (props.closeFormHandler) props.closeFormHandler();

    if (props.closeAddDataFormHandlerExpenseItem) {
      props.closeAddDataFormHandlerExpenseItem();
      setEditModal(true);
    }
  }

  // let classNameAnimation = !animation
  //   ? ""
  //   : animation === "expense"
  //   ? "bump--blue"
  //   : "bump--pink";

  return (
    <Modal>
      <form onSubmit={formSubmitHandler} className={style.form}>
        <div className={style["title__container"]}>
          <InputRadio
            classInput={style.input}
            classCheck={`${style.expense} ${style.check}`}
            classLabel={style.title}
            label="expense"
            name="title"
            value="expense"
            onChange={categoryChangeHandler}
            checked={formData.category === "expense"}
          />
          <InputRadio
            classInput={style.input}
            classCheck={`${style.income} ${style.check}`}
            classLabel={style.title}
            label="income"
            name="title"
            value="income"
            onChange={categoryChangeHandler}
            checked={formData.category === "income"}
          />
        </div>
        <hr className={style.line}></hr>
        <div className={style["form__container"]}>
          <div className={style["container"]}>
            <label className={style.label}>
              main category
              <div className={style.icon}>{iconObj[formData.mainCategory]}</div>
            </label>
            <select
              value={formData.mainCategory}
              onChange={mainCategoryChangeHandler}
              className={style.input}
              type="select"
            >
              {formData.mainCategoryArr.map((element) => (
                <option value={element} key={element}>
                  {element}
                </option>
              ))}
            </select>
          </div>

          <div className={style["container"]}>
            <label className={style.label}>sub category</label>

            <select
              value={formData.subCategory}
              onChange={subCategoryChangeHandler}
              className={style.input}
              type="select"
            >
              {formData.subCategoryArr.map((element) => (
                <option value={element} key={element}>
                  {element}
                </option>
              ))}
            </select>
          </div>
          <div className={style["container"]}>
            <label className={style.label}>description (optional)</label>
            <input
              value={formData.description}
              onChange={descriptionChangeHandler}
              className={style.input}
              type="text"
            ></input>
          </div>

          <div className={style["container"]}>
            <label className={style.label}>date</label>
            <input
              value={formData.date}
              className={style.input}
              type="date"
              onChange={dateChangeHandler}
            ></input>
          </div>

          <div className={`${style["container"]} ${style["container--last"]}`}>
            <label className={style.label}>price</label>
            <input
              onChange={priceChangeHandler}
              className={style.input}
              type="number"
              value={formData.price}
            ></input>
          </div>

          <div className={`${style["btn__container"]} `}>
            <Button
              type="button"
              className={`${style.btn} ${style["btn--isValid"]}`}
              onClick={cancelClickHandler}
            >
              cancel
            </Button>
            <Button
              type="submit"
              className={
                formData.isValid
                  ? `${style["btn--isValid"]} ${style.btn}`
                  : `${style.btn}`
              }
              disabled={!formData.isValid}
            >
              {props.initialObj ? "edit" : "add"}
            </Button>
          </div>
        </div>
      </form>
    </Modal>
  );
}

export default AddDataForm;
