import createWeeklyData from "../createWeeklyData";
import createDateStringFormat from "../CreateDateStringFormat/CreateDateStringFormat";
import timeObj from "../../components/assets/timeObj/timeObj";

function createAccountCardPreData(type) {
  const { TODAY } = timeObj;

  if (type === "week") {
    const labels = ["SUN", "MON", "TUE", "WED", "THR", "FRI", "SAT"];

    const weeklyDataArr = createWeeklyData(TODAY);
    const startingDateOfWeek = weeklyDataArr[0];
    const endingDateOfWeek = weeklyDataArr[weeklyDataArr.length - 1];

    const startingDateString = createDateStringFormat(
      startingDateOfWeek.dateObj
    );
    const endingDateString = createDateStringFormat(endingDateOfWeek.dateObj);

    return [
      startingDateOfWeek.dateObj,
      endingDateOfWeek.dateObj,
      startingDateString,
      endingDateString,
      labels,
    ];
  } else {
    const startingDateOfMonth = new Date(
      TODAY.getFullYear(),
      TODAY.getMonth(),
      1
    );
    const endingDateOfMonth = new Date(
      TODAY.getFullYear(),
      TODAY.getMonth() + 1,
      0
    );

    const startingDateString = createDateStringFormat(startingDateOfMonth);
    const endingDateString = createDateStringFormat(endingDateOfMonth);

    return [
      startingDateOfMonth,
      endingDateOfMonth,
      startingDateString,
      endingDateString,
    ];
  }
}

export default createAccountCardPreData;
