import { useContext } from "react";
import AccountSection from "../../../UI/AccountSection/AccountSection";
import ExpenseDataContext from "../../../../store/expenseData/expenseData--context";
import createIndividualData from "../../../Others/CreateSmallChartData/createIndividualData";
import createConfig from "../../../Others/CreateChartData/createConfig";
import style from "./Weekly.module.css";

const labels = ["MON", "TUE", "WED", "THR", "FRI", "SUN", "SAT"];

const today = new Date(); // get current date
const first = today.getDate() - today.getDay(); // First day is the day of the month - the day of the week
const last = first + 6; // last day is the first day + 6

const firstTest = `${today.getFullYear()}-${
  today.getMonth() + 1 > 10 ? today.getMonth() + 1 : `0${today.getMonth() + 1}`
}-${first - 1 > 10 ? first - 1 : `0${first} - 1`}`;

const lastTest = `${today.getFullYear()}-${
  today.getMonth() + 1 > 10 ? today.getMonth() + 1 : `0${today.getMonth() + 1}`
}-${last > 10 ? last : `0${last}`}`;

function Weekly() {
  const { expenseData, categoryExpense } = useContext(ExpenseDataContext);

  const configBar = createConfig(
    "time",
    "7",
    firstTest,
    lastTest,
    expenseData,
    "expense",
    Object.keys(categoryExpense)
  );

  const configPie = createConfig(
    "",
    "7",
    firstTest,
    lastTest,
    expenseData,
    "expense",
    Object.keys(categoryExpense)
  );

  configBar.data.labels = labels;

  console.log(configPie);

  return (
    <div className={style.weekly}>
      <AccountSection
        title="Weekly Overview"
        income="1000"
        expense="600"
        configBar={configBar}
        configPie={configPie}
      />
    </div>
  );
}

export default Weekly;

