import { useState, useCallback, useContext, useReducer, Fragment } from "react";
import CategoryContext from "../../../../../store/category/category--context";
import InputRadio from "../../../../UI/InputRadio/InputRadio";
import Button from "../../../../UI/Button";
import style from "./SettingCategory.module.css";
import { GoX } from "react-icons/go";

let expenseObj, incomeObj;

function reducer(state, action) {
  switch (action.type) {
    case "FIRST_CATEGORY": {
      let mainCategoryArr, subCategoryArr, mainCategory;
      if (action.value === "expense") {
        mainCategoryArr = Object.keys(expenseObj);
        subCategoryArr = expenseObj[Object.keys(expenseObj)[0]];
        mainCategory = Object.keys(expenseObj)[0];
      } else {
        mainCategoryArr = Object.keys(incomeObj);
        subCategoryArr = incomeObj[Object.keys(incomeObj)[0]];
        mainCategory = Object.keys(incomeObj)[0];
      }

      return {
        ...state,
        category: action.value,
        mainCategoryArr,
        subCategoryArr,
        mainCategory,
        removeMain: false,
        removeSub: false,
      };
    }
    case "MAIN_CATEGORY": {
      let subCategoryArr;

      if (state.category === "expense")
        subCategoryArr = expenseObj[action.value];
      else subCategoryArr = incomeObj[action.value];

      return { ...state, subCategoryArr, mainCategory: action.value };
    }

    case "REMOVE_MAIN_CATEGORY": {
      return { ...state, removeMain: !state.removeMain };
    }

    default:
      break;
  }
}

function SettingCategory(props) {
  const { categoryExpense, categoryIncome, removeCategoryExpense } =
    useContext(CategoryContext);
  const [categoryState, categoryStateDispatch] = useReducer(reducer, {
    category: "expense",
    mainCategory: Object.keys(categoryExpense)[0],
    mainCategoryArr: Object.keys(categoryExpense),
    subCategory: categoryExpense[Object.keys(categoryExpense)[0]][0],
    subCategoryArr: categoryExpense[Object.keys(categoryExpense)[0]],
    removeMain: false,
    removeSub: false,
  });
  expenseObj = categoryExpense;
  incomeObj = categoryIncome;

  const mainDataContent = categoryState.mainCategoryArr.map((element) => (
    <div key={element}>
      <input
        id={element}
        name="mainData"
        className={style.input}
        type="radio"
      ></input>
      <label
        className={`${style.data}  ${
          element === categoryState.mainCategory
            ? categoryState.category === "expense"
              ? style["data--active--expense"]
              : style["data--active--income"]
            : ""
        }`}
      >
        {element}
      </label>
    </div>
  ));

  /*
      <InputRadio
      id={element}
      key={element}
      value={element}
      name="mainData"
      checked={categoryState.mainCategory === element}
      onChange={mainDataClickHandler}
      //   classInput={categoryState.removeMain && style.input}
      classLabel={`${style.data}  ${
        element === categoryState.mainCategory
          ? categoryState.category === "expense"
            ? style["data--active--expense"]
            : style["data--active--income"]
          : ""
      }`}
      label={
        <Fragment>
          {element}
          {categoryState.removeMain && (
            <Button
              onClick={() => {
                console.log("a");
              }}
              className={style["btn--remove"]}
              type="button"
            >
              x
            </Button>
          )}
        </Fragment>
      }
    />
  */

  const subDataContent = categoryState.subCategoryArr.map((element) => (
    <span key={element} className={`${style.data} `}>
      {element}
    </span>
  ));

  function categoryChangeHandler(e) {
    categoryStateDispatch({ type: "FIRST_CATEGORY", value: e.target.value });
  }

  function mainDataClickHandler(e) {
    categoryStateDispatch({
      type: "MAIN_CATEGORY",
      value: e.target.value,
    });
  }

  function removeBtnClickHandler() {
    categoryStateDispatch({
      type: "REMOVE_MAIN_CATEGORY",
    });
  }

  return (
    <form className={style.form}>
      <div className={style["form__list"]}>
        <div className={style.category}>
          <InputRadio
            classContainer={style["radio__container"]}
            classInput={style.input}
            classLabel={`${style.labelBlue} ${style.label}`}
            classCheck={style.check}
            id="expense"
            name="category"
            value="expense"
            label="expense"
            checked={categoryState.category === "expense"}
            onChange={categoryChangeHandler}
          />
          <InputRadio
            classContainer={style["radio__container"]}
            classInput={style.input}
            classLabel={`${style.labelPink} ${style.label}`}
            classCheck={style.check}
            id="income"
            name="category"
            value="income"
            label="income"
            checked={categoryState.category === "income"}
            onChange={categoryChangeHandler}
          />
        </div>
        <div className={style.container}>
          <p className={style.subtitle}>main data</p>
          <div className={style["data__container"]}>{mainDataContent}</div>
          <div className={style["btn__container"]}>
            <Button
              onClick={removeBtnClickHandler}
              type="button"
              className={style.btn}
            >
              remove
            </Button>
            <Button type="button" className={style.btn}>
              add
            </Button>
          </div>
        </div>
        <div className={style.container}>
          <p className={style.subtitle}>sub data</p>
          <div className={style["data__container"]}>{subDataContent}</div>
        </div>
      </div>
    </form>
  );
}

export default SettingCategory;
