import { useRef, useEffect } from "react";
import Title from "../../../UI/Title/Title";
import SubTitle from "../../../UI/SubTitle/SubTitle";
import Card from "../../../UI/Card/Card";
import AccountCard from "./AccountCard";
import style from "./AccountOverview.module.css";
import Chart from "chart.js/auto";

const labels = ["MON", "TUE", "WED", "THR", "FRI", "SUN", "SAT"];
const configArea = {
  type: "line",
  data: {
    labels: labels,
    datasets: [
      {
        fill: {
          target: "origin",
        },
        label: "My First Dataset",
        data: [65, 59, 80, 81, 56, 55, 40],
        backgroundColor: ["rgba(54, 162, 235, 0.2)"],
        borderColor: ["rgb(54, 162, 235)"],
        borderWidth: 1,
        pointBackgroundColor: ["rgb(54, 162, 235)"],
      },
    ],
  },
  options: {
    elements: {
      line: {
        tension: 0.5,
      },
    },
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  },
};

function AccountOverview(props) {
  const chartRef = useRef(null);
  useEffect(() => {
    const chart = new Chart(chartRef.current, configArea);

    return function cleanUp() {
      chart.destroy();
    };
  }, []);

  return (
    <div className={style.accountOverview}>
      <div className={style["title__section"]}>
        <Title>account overview</Title>
        <SubTitle className={style.subTitle}>hi, username</SubTitle>
      </div>
      <div className={style["card__container"]}>
        <Card className={style.card}>
          <SubTitle className={style["card__subtitle"]}>
            <p className={style["card__subtitle--text"]}>income</p>
            <p className={style["card__subtitle--number"]}>$11100</p>
          </SubTitle>
        </Card>
        <Card className={style.card}>
          <SubTitle className={style["card__subtitle"]}>
            <p className={style["card__subtitle--text"]}>expense</p>
            <p className={style["card__subtitle--number"]}>$11100</p>
          </SubTitle>
        </Card>
        <Card className={style.card}>
          <SubTitle className={style["card__subtitle"]}>
            <p className={style["card__subtitle--text"]}>net</p>
            <p className={style["card__subtitle--number"]}>$11100</p>
          </SubTitle>
        </Card>
      </div>
      <div>
        <canvas ref={chartRef}></canvas>
      </div>
    </div>
  );
}

export default AccountOverview;
