import ExpenseDataContext from "../../../store/expenseData/expenseData--context";

function reducer(state, action) {
  switch (action.type) {
    case "LIST": {
      return { ...state, date: action.value };
    }

    case "COLOR": {
      return { ...state, element: action.value };
    }

    default:
      break;
  }
}

