import { useState, useCallback, useContext } from "react";
import CategoryContext from "../../../../../store/category/category--context";
import InputRadio from "../../../../UI/InputRadio/InputRadio";
import Button from "../../../../UI/Button";
import style from "./SettingCategory.module.css";
import { GoX } from "react-icons/go";

function SettingCategory(props) {
  const { categoryExpense, categoryInome } = useContext(CategoryContext);
  const [category, setCategory] = useState("expense");
  const [mainData, setMainData] = useState(
    category === "expense"
      ? Object.keys(categoryExpense)[0]
      : Object.keys(categoryInome)[0]
  );
  const [remove, setRemove] = useState(false);

  const mainDataArr =
    category === "expense"
      ? Object.keys(categoryExpense)
      : Object.keys(categoryInome);

  const subDataArr =
    category === "expense"
      ? categoryExpense[mainData]
      : categoryInome[mainData];

  const mainDataContent = mainDataArr.map((element) => (
    <span
      key={element}
      onClick={mainDataClickHandler}
      className={`${style.data} ${
        element === mainData ? `${style["data--active"]}` : ""
      }`}
    >
      {element}
      {remove && <GoX className={style["icon--remove"]} />}
    </span>
  ));

  console.log(category);
  console.log(mainData);
  console.log(subDataArr);

  //   const subDataContent = subDataArr.map((element) => (
  //     <span key={element} className={`${style.data} `}>
  //       {element}
  //     </span>
  //   ));

  const subDataContent = [];

  function categoryChangeHandler(e) {
    setCategory(e.target.value);
  }

  function mainDataClickHandler(e) {
    setMainData(e.target.textContent);
  }

  function removeBtnClickHandler() {
    setRemove((prev) => !prev);
  }

  return (
    <form className={style.form}>
      <div className={style["form__list"]}>
        <div className={style.category}>
          <InputRadio
            classContainer={style["radio__container"]}
            classInput={style.input}
            classLabel={`${style.labelBlue} ${style.label}`}
            classCheck={`${style.checkBlue} ${style.check}`}
            classInside={`${style.insideBlue} ${style.inside}`}
            id="expense"
            name="category"
            value="expense"
            label="expense"
            checked={category === "expense"}
            onChange={categoryChangeHandler}
          />
          <InputRadio
            classContainer={style["radio__container"]}
            classInput={style.input}
            classLabel={`${style.labelGreen} ${style.label}`}
            classCheck={`${style.checkGreen} ${style.check}`}
            classInside={`${style.insideGreen} ${style.inside}`}
            id="income"
            name="category"
            value="income"
            label="income"
            checked={category === "income"}
            onChange={categoryChangeHandler}
          />
        </div>
        <div className={style.container}>
          <p className={style.subtitle}>main data</p>
          <div className={style["data__container"]}>{mainDataContent}</div>
          <div className={style["btn__container"]}>
            <Button
              onClick={removeBtnClickHandler}
              type="button"
              className={style.btn}
            >
              remove
            </Button>
            <Button type="button" className={style.btn}>
              add
            </Button>
          </div>
        </div>
        <div className={style.container}>
          <p className={style.subtitle}>sub data</p>
          <div className={style["data__container"]}>{subDataContent}</div>
        </div>
      </div>
    </form>
  );
}

export default SettingCategory;
