import createStandardLabelsArr from "./createStandardLabelsArr";
import createLabelsArr from "./createLabelsArr";
import createBarDataArr from "./createBarDataArr";
import createPieDataArr from "./createPieDataArr";
import createLineDataArr from "./createLineDataArr";
import createLabelsAccount from "../CreateAccountData/createLabelsAccount";
import createChartDataArr from "../CreateAccountData/CreateChartDataArr";
import createFilteredData from "./createFilteredData";

function createFinalDataArr(
  mainType,
  timeDuration,
  startingDate,
  endingDate,
  expenseData,
  mainCategory,
  subCategory,
  showLabel = false,
  mainCategoryData = [],
  subCategoryData = []
) {
  if (mainType === "time") {
    const standardLabels = createStandardLabelsArr(timeDuration, startingDate);
    const labels = createLabelsArr(standardLabels, timeDuration);

    if (mainCategory === "net") {
      const data = createLineDataArr(
        standardLabels,
        createFilteredData(standardLabels, expenseData),
        timeDuration
      );

      return [labels, data];
    } else {
      const labels = createLabelsAccount(
        standardLabels[0],
        standardLabels[standardLabels.length - 1]
      );
      const data = createChartDataArr(
        labels,
        createFilteredData(standardLabels, expenseData)
      );
      //   const data = createBarDataArr(
      //     standardLabels,
      //     createFilteredData(standardLabels, expenseData),
      //     timeDuration,
      //     mainCategory,
      //     subCategory,
      //     showLabel,
      //     mainCategoryData,
      //     subCategoryData
      //   );

      return [labels, data];
    }
  } else {
    const [labels, data] = createPieDataArr(
      startingDate,
      endingDate,
      expenseData,
      mainCategory
    );

    return [labels, data];
  }
}

export default createFinalDataArr;
