import { useState } from "react";

import MainContent from "./components/MainContent/MainContent";
import SideBar from "./components/SideBar/SideBar";
import AddDataForm from "./components/UI/AddDataForm/AddDateForm";
import ExpenseListModal from "./components/UI/ExpenseListModal/ExpenseListModal";
import Modal from "./components/UI/Modal/Modal";
import ModalShownContext from "./store/modalShown/modalShown--context";
import ExpenseDataContext from "./store/expenseData/expenseData--context";

import style from "./App.module.css";

const TODAY = new Date();

const EXPENSE_DATA = [
  {
    category: "expense",
    mainCate: "food",
    subCate: "breakfast",
    time: "2021-08-01",
    description: "Burger",
    money: "50",
  },
  {
    category: "expense",
    mainCate: "clothing",
    subCate: "t-shirt",
    time: "2021-04-11",
    money: "100",
  },
  {
    category: "expense",
