    subCate: "bill",
    time: "2021-10-23",
    money: "500",
  },
  {
    category: "expense",
    mainCate: "transportation",
    subCate: "bus",
    time: "2021-02-15",
    description: "Burger",
    money: "10",
  },
  {
    category: "income",
    mainCate: "salary",
    subCate: "salary",
    time: "2021-09-13",
    money: "3000",
  },
  {
    category: "expense",
    mainCate: "housing",
    subCate: "bill",
    time: "2021-09-08",
    money: "500",
  },
  {
    category: "expense",
    mainCate: "food",
    subCate: "dinner",
    time: "2021-09-01",
    description: "Burger",
    money: "110",
  },
  {
    category: "income",
    mainCate: "stock",
    subCate: "stock",
    time: "2021-09-01",
    money: "100",
  },
  {
    category: "expense",
    mainCate: "education",
    subCate: "book",
    time: "2021-09-11",
    money: "50",
  },
  {
    category: "income",
    mainCate: "salary",
    subCate: "salary",
    time: "2021-09-18",
    money: "1000",
  },
  {
    category: "expense",
    mainCate: "entertainment",
    subCate: "video games",
    time: "2021-09-23",
    description: "Burger",
    money: "110",
  },
  {
    category: "income",
    mainCate: "bonus",
    subCate: "bonus",
    time: "2021-09-12",
    money: "500",
  },
  {
    category: "expense",
    mainCate: "food",
    subCate: "BreakFast",
    time: "2021-09-22",
    description: "Burger",
    money: "110",
  },
  {
    category: "income",
    mainCate: "others",
    subCate: "others",
    time: "2021-09-21",
    money: "100",
  },
];

function App() {
  const [page, setPage] = useState(0);
  const [addDataFormModalShown, setAddDataFormModalShown] = useState(false);
  const [expenseListModalShown, setExpenseListModalShown] = useState(false);
  const [expenseListData, setExpenseListData] = useState();
  const [dateOnForm, setDateOnForm] = useState();

  function getData(data) {
    setExpenseListData(data);
  }

  const contextValueObj = {
    setAddDataFormModalShown,
    setExpenseListModalShown,
    getData,
  };

  return (
    <ExpenseDataContext.Provider value={{ EXPENSE_DATA }}>
      <ModalShownContext.Provider value={contextValueObj}>
        {addDataFormModalShown && (
          <Modal content={<AddDataForm date={dateOnForm} />} />
        )}
        {expenseListModalShown && (
          <Modal content={<ExpenseListModal data={expenseListData} />} />
        )}

        <div className={`${style["app__container"]} center`}>
          <SideBar today={TODAY} setPage={setPage} page={page} />

          <MainContent today={TODAY} page={page} />
        </div>
      </ModalShownContext.Provider>
    </ExpenseDataContext.Provider>
  );
}

export default App;

