import { useState, Fragment } from "react";

import MainContent from "./components/MainContent/MainContent";
import SideBar from "./components/SideBar/SideBar";
import Modal from "./components/UI/Modal/Modal";
import FormContext from "./store/formShown/form--context";

import style from "./App.module.css";

const TODAY = new Date();

// var dt = new Date();

// dt.getMonth() will return a month between 0 - 11
// we add one to get to the last day of the month
// so that when getDate() is called it will return the last day of the month
// var month = dt.getMonth() + 1;

// var year = dt.getFullYear();

// this line does the magic (in collab with the lines above)
// var daysInMonth = new Date(year, month, 0).getDate();

function App() {
  const [page, setPage] = useState(0);
  const [modalShown, setModalShown] = useState(true);

  return (
    <Fragment>
      {modalShown && <Modal setModalShown={setModalShown} />}

      <div className={`${style["app__container"]} center`}>
        <SideBar today={TODAY} setPage={setPage} page={page} />

        <FormContext.provider value={setModalShown}>
          <MainContent
            setModalShown={setModalShown}
            today={TODAY}
            page={page}
          />
        </FormContext.provider>
      </div>
    </Fragment>
  );
}

export default App;
