function aaaaa() {
  const date = new Date();

  const res = [];

  // last day of current month
  // 0 gives us the last day of previous month
  // + 1 makes to be last day fo current month
  // get date gives us the number type
  const lastDay = new Date(
    date.getFullYear(),
    date.getMonth() + 1,
    0
  ).getDate();

  const prevLastDay = new Date(
    date.getFullYear(),
    date.getMonth(),
    0
  ).getDate(); // last day of previous month

  // last day of the week (current month)
  const lastDayIndex = new Date(
    date.getFullYear(),
    date.getMonth() + 1,
    0
  ).getDay();

  // first day of the week (current month)
  const firstDayIndex = date.getDay();
  console.log(firstDayIndex);

  for (let i = firstDayIndex; i > 0; i--) {
    res.push(prevLastDay - i + 1);
  }

  for (let i = 0; i < lastDay; i++) {
    res.push(i);
  }

  console.log(res);
}

export default aaaaa;
