import compareTimeWithRange from "../CompareTime/compareTimeWithRange";

function createPieDataArr(expenseData, mainCategory) {
  const filteredData = expenseData.filter(
    (element) =>
      compareTimeWithRange(
        element,
        new Date(startingDate),
        new Date(endingDate)
      ) && element.type === mainCategory
  );

  // Reference 1
  const dataObj = {};
  for (let data of filteredData) {
    if (dataObj[data.mainCategory] !== undefined)
      dataObj[data.mainCategory] += data.price;
    else dataObj[data.mainCategory] = data.price;
  }

  return [Object.keys(dataObj), Object.values(dataObj)];
}

export default createPieDataArr;

/*
Reference 1
Key Part
Along with the process iterating the filteredData
1. add the new label as key if the label hasn't been added before
2. accumulate the price to corresponding label if the label has been added
*/
