import React from "react";
import { v4 as uuidv4 } from "uuid";
import style from "./createCalendar.module.css";
import FormContext from "../../../../store/formShown/form--context";

const DATE = new Date();

const MONTH = DATE.getMonth();
const TODAY = DATE.getDate();

const EXPENSE_DATA = [
  {
    category: "expense",
    mainCate: "food",
    subCate: "breakfast",
    time: "2021-08-01",
    description: "Burger",
    money: "50",
  },
  {
    category: "expense",
    mainCate: "clothing",
    subCate: "t-shirt",
    time: "2021-04-11",
    money: "100",
  },
  {
    category: "expense",
    mainCate: "housing",
    subCate: "bill",
    time: "2021-10-23",
    money: "500",
  },
  {
    category: "expense",
    mainCate: "transportation",
    subCate: "bus",
    time: "2021-02-15",
    description: "Burger",
    money: "10",
  },
  {
    category: "income",
    mainCate: "salary",
    subCate: "salary",
    time: "2021-09-13",
    money: "3000",
  },
  {
    category: "expense",
    mainCate: "housing",
    subCate: "bill",
    time: "2021-09-08",
    money: "500",
  },
  {
    category: "expense",
    mainCate: "food",
    subCate: "dinner",
    time: "2021-09-01",
    description: "Burger",
    money: "110",
  },
  {
    category: "income",
    mainCate: "stock",
    subCate: "stock",
    time: "2021-09-01",
    money: "100",
  },
  {
    category: "expense",
    mainCate: "education",
    subCate: "book",
    time: "2021-09-11",
    money: "50",
  },
  {
    category: "income",
    mainCate: "salary",
    subCate: "salary",
    time: "2021-09-18",
    money: "1000",
  },
  {
    category: "expense",
    mainCate: "entertainment",
    subCate: "video games",
    time: "2021-09-23",
    description: "Burger",
    money: "110",
  },
  {
    category: "income",
    mainCate: "bonus",
    subCate: "bonus",
    time: "2021-09-12",
    money: "500",
  },
  {
    category: "expense",
    mainCate: "food",
    subCate: "BreakFast",
    time: "2021-09-22",
    description: "Burger",
    money: "110",
  },
  {
    category: "income",
    mainCate: "others",
    subCate: "others",
    time: "2021-09-21",
    money: "100",
  },
];

const cacheObj = {};

function createCalendar(date) {
  const key = date.getFullYear() + "" + date.getMonth();

  if (!cacheObj[key]) {
    cacheObj[key] = [];

    const TIME_EXPENSE = EXPENSE_DATA.filter((element) => {
      if (
        Number(element.time.slice(0, 4)) === date.getFullYear() &&
        Number(element.time.slice(5, 7)) === date.getMonth() + 1 &&
        element.category === "expense"
      )
        return true;
    }).map((element) => Number(element.time.slice(8)));

    cacheObj[key].push(TIME_EXPENSE);

    const TIME_INCOME = EXPENSE_DATA.filter((element) => {
      if (
        Number(element.time.slice(0, 4)) === date.getFullYear() &&
        Number(element.time.slice(5, 7)) === date.getMonth() + 1 &&
        element.category === "income"
      )
        return true;
    }).map((element) => Number(element.time.slice(8)));

    cacheObj[key].push(TIME_INCOME);
  }

  const res = [];

  // last day of current month
  // 0 gives us the last day of previous month
  // + 1 makes to be last day fo current month
  // get date gives us the number type
  const lastDay = new Date(
    date.getFullYear(),
    date.getMonth() + 1,
    0
  ).getDate();

  const prevLastDay = new Date(
    date.getFullYear(),
    date.getMonth(),
    0
  ).getDate(); // last day of previous month

  // last day of the week (current month)
  const lastDayIndex = new Date(
    date.getFullYear(),
    date.getMonth() + 1,
    0
  ).getDay();

  // first day of the week (current month)
  // const firstDayIndex = date.getDay();
  const firstDayIndex = new Date(
    date.getFullYear(),
    date.getMonth(),
    1
  ).getDay();

  const nextDays = 7 - lastDayIndex - 1;

  for (let i = firstDayIndex; i > 0; i--) {
    res.push(
      <div className={style.negative} key={uuidv4()}>
        {prevLastDay - i + 1}
      </div>
    );
  }

  for (let i = 1; i <= lastDay; i++) {
    if (i === TODAY && date.getMonth() === MONTH) {
      res.push(checkDot(i, true, cacheObj[key][0], cacheObj[key][1]));
    } else res.push(checkDot(i, false, cacheObj[key][0], cacheObj[key][1]));
  }

  for (let i = 1; i <= nextDays; i++) {
    res.push(
      <div className={style.negative} key={uuidv4()}>
        {i}
      </div>
    );
  }

  return res;
}

export default createCalendar;

function checkDot(i, current, TIME_EXPENSE, TIME_INCOME) {
  if (TIME_EXPENSE.includes(i)) {
    if (TIME_INCOME.includes(i)) {
      return (
        <div className={current ? style.current : ""} key={uuidv4()}>
          {i}
          <div className={style["dot__container"]}>
            <span className={`${style["dot--blue"]} ${style.dot}`}></span>
            <span className={`${style["dot--pink"]} ${style.dot}`}></span>
          </div>
        </div>
      );
    } else {
      return (
        <div className={current ? style.current : ""} key={uuidv4()}>
          {i}
          <div className={style["dot__container"]}>
            <span className={`${style["dot--blue"]} ${style.dot}`}></span>
          </div>
        </div>
      );
    }
  } else if (TIME_INCOME.includes(i)) {
    return (
      <div className={current ? style.current : ""} key={uuidv4()}>
        {i}
        <div className={style["dot__container"]}>
          <span className={`${style["dot--pink"]} ${style.dot}`}></span>
        </div>
      </div>
    );
  } else {
    return (
      <div className={current ? style.current : ""} key={uuidv4()}>
        {i}
      </div>
    );
  }
}
