import SubTitle from "../../../../UI/SubTitle/SubTitle";
import InputCheckbox from "../../../../UI/InputCheckbox/InputCheckbox";
import style from "./OptionCheckbox.module.css";

function ChartOptionCheckbox(props) {
  function checkboxChangeHandler(e) {
    props.dispatchChartData({
      type: "SUB_CATEGORY",
      value: e.target.value,
      check: e.target.checked,
    });
  }

  return (
    <div className={style.checkbox}>
      <SubTitle className={style["subTitle--blue"]}>Select Sub Data</SubTitle>
      <div className={style.test}>
        <div className={style["checkbox__container"]}>
          {props.category.map((element) => (
            <InputCheckbox
              classContainer={style["input__container"]}
              classCheck={style.check}
              classInput={style.input}
              classLabel={style.label}
              key={element}
              id={element}
              label={`${element.substr(0, 1).toUpperCase()}${element.substr(
                1,
                element.length - 1
              )}`}
              value={element}
              defaultChecked={true}
              onChange={checkboxChangeHandler}
            />
          ))}
        </div>
      </div>
    </div>
  );
}

export default ChartOptionCheckbox;
