import { useContext, useState } from "react";
import Title from "../../../UI/Title/Title";
import SubTitle from "../../../UI/SubTitle/SubTitle";
import AccountCard from "./AccountCard";
import AccountChart from "./AccountChart";
import InputRadio from "../../../UI/InputRadio/InputRadio";
import ExpenseDataContext from "../../../../store/expenseData/expenseData--context";
import createFilteredData from "../../../Others/CreateChartData/createFilteredData";
import createDateFormat from "../../../Others/createDateFormat";
import style from "./AccountOverview.module.css";

function AccountOverview(props) {
  return (
    <div className={style.accountOverview}>
      <div className={style["title__section"]}>
        <Title>account overview</Title>
        <SubTitle className={style.subTitle}>hi, username</SubTitle>
      </div>
      <AccountCard />
      <div
        onChange={chartRadioChangeHandler}
        className={style["radio__container"]}
      >
        {chartRadioArr.map((element, index) => (
          <InputRadio
            key={element}
            label={element}
            id={element}
            value={element}
            name="chart__btn"
            classLabel={style.label}
            classInput={style.input}
            classCheck={style.check}
          />
        ))}
      </div>
      <AccountChart labels={chartLabel} data={chartData} />
    </div>
  );
}

export default AccountOverview;

function createChartDataArr(labels, expenseData) {
  const newExpenseData = createFilteredData(labels, expenseData);

  const newExpenseDataExpense = newExpenseData.filter(
    (element) => element.category === "expense"
  );

  const newExpenseDataIncome = newExpenseData.filter(
    (element) => element.category === "income"
  );

  const dataArrExpense = createDataArrAccount(labels, newExpenseDataExpense);
  const dataArrIncome = createDataArrAccount(labels, newExpenseDataIncome);

  const finalDataArr = [];

  dataArrExpense.forEach((expense, index) => {
    finalDataArr.push(dataArrIncome[index] - expense);
  });

  return finalDataArr;
}

function createDataArrAccount(labels, expensData) {
  const data = [];
  let indexCurrentDate = true;
  let currentAmount = 0;
  let indexData = 0;
  let yearLabel, monthLabel, dayLabel, yearDate, monthData, dayData;

  for (let i = 0; i < labels.length; i++) {
    yearLabel = labels[i].slice(0, 4);
    monthLabel = labels[i].slice(5, 7);
    dayLabel = labels[i].slice(8);

    while (indexCurrentDate) {
      yearDate = expensData[indexData]?.time.slice(0, 4);
      monthData = expensData[indexData]?.time.slice(5, 7);
      dayData = expensData[indexData]?.time.slice(8);

      if (
        yearLabel === yearDate &&
        monthLabel === monthData &&
        dayLabel >= dayData
      ) {
        currentAmount += Number(expensData[indexData].price);
        indexData++;
      } else indexCurrentDate = false;
    }

    data.push(currentAmount);
    indexCurrentDate = true;
  }

  return data;
}

function createLabelsAccount(startDate, endDate) {
  const label = [];
  const diff = Math.round(
    (new Date(endDate).getTime() - new Date(startDate).getTime()) /
      (1000 * 3600 * 24)
  );

  let eachDiffAmount;
  let lastDiffAmount = 0;
  if (diff % 7 === 0) eachDiffAmount = diff / 7;
  else {
    eachDiffAmount = (diff - (diff % 7)) / 7;
    lastDiffAmount = diff - eachDiffAmount * 6;
  }

  let tmpDate;
  for (let i = 0; i < 6; i++) {
    tmpDate = new Date(startDate.setDate(startDate.getDate() + eachDiffAmount));
    label.push(createDateFormat(tmpDate));
  }

  if (lastDiffAmount === 0)
    tmpDate = new Date(startDate.setDate(startDate.getDate() + eachDiffAmount));
  else
    tmpDate = new Date(startDate.setDate(startDate.getDate() + lastDiffAmount));

  label.push(createDateFormat(tmpDate));

  return label;
}
