import { useState, useReducer } from "react";
import InputRadio from "../../../../UI/InputRadio/InputRadio";
import Button from "../../../../UI/Button";
import style from "./SettingCategory.module.css";
import { GoX } from "react-icons/go";

const EXPENSE_CATEGORY = {
  food: ["breakfast", "brunch", "lunch", "dinner", "snack", "drink"],
  clothing: ["clothes", "pants", "shoes", "accessories", "underwear"],
  housing: [
    "rent",
    "water  bill",
    "electricity bill",
    "gas bill",
    "cable bill",
    "internet bill",
  ],
  transportation: ["gasoline", "parking", "maintenance"],
  education: ["books", "course"],
  entertainment: ["video games", "computer games", "party"],
  others: ["others"],
};

const INCOME_CATEGORY = {
  salary: ["salary", "bonus"],
  stock: ["stock"],
  others: ["others"],
};

function SettingCategory(props) {
  const [mainData, setMainData] = useState("food");
  const [remove, setRemove] = useState(false);

  const mainDataArr = Object.keys(EXPENSE_CATEGORY).map((element) => (
    <span
      key={element}
      onClick={mainDataClickHandler}
      className={`${style.data} ${
        element === mainData ? `${style["data--active"]}` : ""
      }`}
    >
      {element}
      {remove && <GoX className={style["icon--remove"]} />}
    </span>
  ));

  const subDataArr = EXPENSE_CATEGORY[mainData].map((element) => (
    <span key={element} className={`${style.data} `}>
      {element}
    </span>
  ));

  function mainDataClickHandler(e) {
    setMainData(e.target.textContent);
  }

  function removeBtnClickHandler() {
    setRemove((prev) => !prev);
  }

  return (
    <form className={style.form}>
      <div className={style["form__list"]}>
        <div className={style.category}>
          <InputRadio
            classContainer={style["radio__container"]}
            classInput={style.input}
            classLabel={`${style.labelBlue} ${style.label}`}
            classCheck={`${style.checkBlue} ${style.check}`}
            classInside={`${style.insideBlue} ${style.inside}`}
            id="time"
            name="chart"
            value="time"
            label="time"
          />
          <InputRadio
            classContainer={style["radio__container"]}
            classInput={style.input}
            classLabel={`${style.labelGreen} ${style.label}`}
            classCheck={`${style.checkGreen} ${style.check}`}
            classInside={`${style.insideGreen} ${style.inside}`}
            id="category"
            name="chart"
            value="category"
            label="category"
          />
        </div>
        <div className={style.container}>
          <p className={style.subtitle}>main data</p>
          <div className={style["data__container"]}>{mainDataArr}</div>
          <div className={style["btn__container"]}>
            <Button
              onClick={removeBtnClickHandler}
              type="button"
              className={style.btn}
            >
              remove
            </Button>
            <Button type="button" className={style.btn}>
              add
            </Button>
          </div>
        </div>
        <div className={style.container}>
          <p className={style.subtitle}>sub data</p>
          <div className={style["data__container"]}>{subDataArr}</div>
        </div>
      </div>
    </form>
  );
}

export default SettingCategory;
