import { useState, useContext } from "react";
import ExpenseDataContext from "../../store/expenseData/expenseData--context";

function useDailyExpenseData(date) {
  const [dailyExpenseList, setDailyExpenseList] = useState(date);
  const { expenseData } = useContext(ExpenseDataContext);

  const dayData = new Date(dailyExpenseList);
  const day = String(dayData.getDate());
  const month = String(dayData.getMonth() + 1);
  const year = String(dayData.getFullYear());

  const newDayData = expenseData.filter((element) => {
    element.year === year && element.year === month && element.year === day;
  });

  console.log(newDayData);
  return [newDayData, setDailyExpenseList];
}

export default useDailyExpenseData;
