import { useState } from "react";
import ChartPic from "./ChartPic/ChartPic";
import ChartOption from "./ChartOption/ChartOption";
import Button from "../../UI/Button";
import ModalCloseIcon from "../../UI/ModalCloseIcon/ModalCloseIcon";
import style from "./Chart.module.css";

function Chart() {
  const [chartData, setChartData] = useState();
  const [chartOptionModal, setChartOptionModal] = useState(false);

  function chartOptionModalToggler() {
    setChartOptionModal((prev) => !prev);
  }

  return (
    <div className={style.chart}>
      <ChartOption
        chartOptionModal={chartOptionModal}
        setChartData={setChartData}
      />
      {chartData === undefined ? (
        <div className={style.container}>
          <p>please input data to create graph</p>
          <Button onClick={chartOptionModalToggler} className={style.btn}>
            add data
          </Button>
        </div>
      ) : (
        <ChartPic className={style["chart__pic"]} chartData={chartData} />
      )}
      {/* {chartOptionModal && (
        <ModalCloseIcon>
          <ChartOption setChartData={setChartData} />
        </ModalCloseIcon>
      )} */}
    </div>
  );
}

export default Chart;
