import { useContext } from "react";
import AccountSection from "../../../UI/AccountSection/AccountSection";
import ExpenseDataContext from "../../../../store/expenseData/expenseData--context";
import DisplayThemeContext from "../../../../store/displayTheme/displayTheme--context";
import createWeeklyData from "../../../../Others/createWeeklyData";
import createDateStringFormat from "../../../../Others/CreateDateStringFormat/CreateDateStringFormat";
import createSmallChartData from "../../../../Others/createSmallChartData";
import createAccAmount from "../../../../Others/createAccAmount";

import style from "./Weekly.module.css";

const labels = ["SUN", "MON", "TUE", "WED", "THR", "FRI", "SAT"];

const weeklyDataArr = createWeeklyData(new Date());
const first = weeklyDataArr[0];
const last = weeklyDataArr[weeklyDataArr.length - 1];

const startingDate = createDateStringFormat(first.dateObj);
const endingDate = createDateStringFormat(last.dateObj);

function Weekly() {
  const { expenseData, categoryExpense } = useContext(ExpenseDataContext);
  const { displayTheme } = useContext(DisplayThemeContext);

  const [configBar, configPie] = createSmallChartData(
    expenseData,
    "7",
    startingDate,
    endingDate,
    categoryExpense,
    displayTheme
  );

  const [accIncome, accExpense] = createAccAmount(
    expenseData,
    true,
    first.dateObj,
    last.dateObj
  );

  configBar.data.labels = labels;

  return (
    <div className={style.weekly}>
      <AccountSection
        title="Weekly Overview"
        income={accIncome}
        expense={accExpense}
        configBar={configBar}
        configPie={configPie}
      />
    </div>
  );
}

export default Weekly;

