import { useRef, useEffect, useState, useContext } from "react";
import Title from "../../../UI/Title/Title";
import ExpenseDataContext from "../../../../store/expenseData/expenseData--context";
import Card from "../../../UI/Card/Card";
import style from "./AccountSmallChart.module.css";
import Chart from "chart.js/auto";

const data = [0, 0, 0];
const labels = [];

const configBar = {
  type: "bar",
  data: {
    labels,
    datasets: [
      {
        label: "",
        data: data,
        backgroundColor: ["rgba(54, 162, 235, 0.2)"],
        borderColor: ["rgb(54, 162, 235)"],
        borderWidth: 1,
        pointBackgroundColor: ["rgb(54, 162, 235)"],
      },
    ],
  },
  options: {
    indexAxis: "y",
  },
};

function AccountSmallChart(props) {
  const { expenseData, categoryExpense } = useContext(ExpenseDataContext);
  const chartRef = useRef(null);

  const newFilteredExpenseData = expenseData.filter(
    (element) => element.category === "expense"
  );

  useEffect(() => {
    for (const key of Object.keys(categoryExpense)) {
      const amount = createDiffCategoryTotalAmount(key, newFilteredExpenseData);

      if (amount > data[0]) {
        data[0] = amount;
        labels[0] = key;
        continue;
      }
      if (amount <= data[0] && amount > data[1]) {
        data[1] = amount;
        labels[1] = key;
        continue;
      }
      if (amount <= data[1] && amount > data[2]) {
        data[2] = amount;
        labels[2] = key;
        continue;
      }
    }

    const chart = new Chart(chartRef.current, configBar);

    return function cleanUp() {
      chart.destroy();
    };
  }, []);

  return (
    <Card className={style["chart__section"]}>
      <Title className={style.title}>Chart</Title>
      <select className={style.select}>
        <option value="0">which category expense the most</option>
        <option value="1">which day expense the most</option>
        <option value="2">which month expense the most</option>
      </select>
      <div className={style["chart__container"]}>
        <canvas ref={chartRef} height="200" width="auto"></canvas>
      </div>
    </Card>
  );
}

export default AccountSmallChart;

function createDiffCategoryTotalAmount(category, expenseData) {
  const newFilteredData = expenseData.filter(
    (element) => element.mainCate === category
  );

  const totalAmount = newFilteredData.reduce(
    (acc, cur) => acc + Number(cur.price),
    0
  );

  return totalAmount;
}
