    description: "Burger",
    money: "110",
  },
  {
    category: "income",
    mainCate: "stock",
    subCate: "stock",
    time: "2021-09-01",
    money: "100",
  },
  {
    category: "expense",
    mainCate: "education",
    subCate: "book",
    time: "2021-09-11",
    money: "50",
  },
  {
    category: "income",
    mainCate: "salary",
    subCate: "salary",
    time: "2021-09-18",
    money: "1000",
  },
  {
    category: "expense",
    mainCate: "entertainment",
    subCate: "video games",
    time: "2021-09-23",
    description: "Burger",
    money: "110",
  },
  {
    category: "income",
    mainCate: "bonus",
    subCate: "bonus",
    time: "2021-09-12",
    money: "500",
  },
  {
    category: "expense",
    mainCate: "food",
    subCate: "BreakFast",
    time: "2021-09-22",
    description: "Burger",
    money: "110",
  },
  {
    category: "income",
    mainCate: "others",
    subCate: "others",
    time: "2021-09-21",
    money: "100",
  },
];

function App() {
  const [page, setPage] = useState(0);
  const [modalShown, setModalShown] = useState(false);
  const [expenseListModalShown, setExpenseListModalShown] = useState(true);

  return (
    <ExpenseDataContext.Provider value={{ EXPENSE_DATA }}>
      <ModalShownContext.Provider
        value={{ setModalShown, setExpenseListModalShown }}
      >
        {modalShown && <Modal content={<AddDataForm />} />}
        {expenseListModalShown && <Modal content={<ExpenseListModal />} />}

        <div className={`${style["app__container"]} center`}>
          <SideBar today={TODAY} setPage={setPage} page={page} />

          <MainContent
            setModalShown={setModalShown}
            today={TODAY}
            page={page}
          />
        </div>
      </ModalShownContext.Provider>
    </ExpenseDataContext.Provider>
  );
}

export default App;

